import React, {Component} from 'react';

class HotWord extends Component {

    componentDidMount() {
        var colorList = [
            [
                '#ff7f50',
                '#87cefa',
                '#da70d6',
                '#32cd32',
                '#6495ed',
                '#ff69b4',
                '#ba55d3',
                '#cd5c5c',
                '#ffa500',
                '#40e0d0',
                '#1e90ff',
                '#ff6347',
                '#7b68ee',
                '#d0648a',
                '#ffd700',
                '#6b8e23',
                '#4ea397',
                '#3cb371',
                '#b8860b',
                '#7bd9a5',
            ],
            [
                '#ff7f50',
                '#87cefa',
                '#da70d6',
                '#32cd32',
                '#6495ed',
                '#ff69b4',
                '#ba55d3',
                '#cd5c5c',
                '#ffa500',
                '#40e0d0',
                '#1e90ff',
                '#ff6347',
                '#7b68ee',
                '#00fa9a',
                '#ffd700',
                '#6b8e23',
                '#ff00ff',
                '#3cb371',
                '#b8860b',
                '#30e0e0',
            ],
            [
                '#929fff',
                '#9de0ff',
                '#ffa897',
                '#af87fe',
                '#7dc3fe',
                '#bb60b2',
                '#433e7c',
                '#f47a75',
                '#009db2',
                '#024b51',
                '#0780cf',
                '#765005',
                '#e75840',
                '#26ccd8',
                '#3685fe',
                '#9977ef',
                '#f5616f',
                '#f7b13f',
                '#f9e264',
                '#50c48f',
            ],
        ][2];

        option = {
            title: {
                show: true,
                text: '一周热词',
                left: 'center',
                top: 20,
                textStyle: {
                    fontSize: 18,
                    fontWeight: 'bolder',
                    color: '#333',
                },
            },
            tooltip: {},
            animationDurationUpdate: function (idx) {
                // 越往后的数据延迟越大
                return idx * 100;
            },
            animationEasingUpdate: 'bounceIn',
            color: ['#fff', '#fff', '#fff'],
            series: [
                {
                    type: 'graph',
                    layout: 'force',
                    force: {
                        repulsion: 500,
                        edgeLength: 10,
                    },
                    roam: true,
                    label: {
                        normal: {
                            show: true,
                        },
                    },
                    data: [
                        {
                            name: '五一小长假',
                            value: 12373,
                            symbolSize: 88,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[0],
                                    color: colorList[0],
                                },
                            },
                        },
                        {
                            name: '考勤规则',
                            value: 2289,
                            symbolSize: 67,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[2],
                                    color: colorList[2],
                                },
                            },
                        },
                        {
                            name: '福利平台',
                            value: 1952,
                            symbolSize: 55,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[5],
                                    color: colorList[5],
                                },
                            },
                        },
                        {
                            name: '疫苗预约',
                            value: 926,
                            symbolSize: 70,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[6],
                                    color: colorList[6],
                                },
                            },
                        },
                        {
                            name: '中美部长会谈',
                            value: 4536,
                            symbolSize: 67,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[7],
                                    color: colorList[7],
                                },
                            },
                        },
                        {
                            name: '与时光赛跑',
                            value: 84000,
                            symbolSize: 134,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[12],
                                    color: colorList[12],
                                },
                            },
                        },
                        {
                            name: '清澈的爱只为中国',
                            value: 27000,
                            symbolSize: 114,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[16],
                                    color: colorList[16],
                                },
                            },
                        },
                        {
                            name: '我们5岁了',
                            value: 103000,
                            symbolSize: 130,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[13],
                                    color: colorList[13],
                                },
                            },
                        },
                        {
                            name: 'IPO上市',
                            value: 92000,
                            symbolSize: 123,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[18],
                                    color: colorList[18],
                                },
                            },
                        },
                        {
                            name: '印度崩溃',
                            value: 20000,
                            symbolSize: 141,
                            draggable: true,
                            itemStyle: {
                                normal: {
                                    shadowBlur: 100,
                                    shadowColor: colorList[19],
                                    color: colorList[19],
                                },
                            },
                        },
                    ],
                },
            ],
        };

    }

    render() {
        return (
            <div id='main' ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px', marginTop: '20px'}}/>
        );
    }
}

export default HotWord;
