import React, {Component} from 'react';
import {Menu} from "antd";
import {AppstoreAddOutlined, MailOutlined, UsergroupAddOutlined} from "@ant-design/icons";
import {withRouter} from "react-router";

import navStyle from './index.module.css';


const {SubMenu}=Menu;

class Nav extends Component {

    state = {height: '1000px'};

    componentDidMount() {
        this.setState({height: `${window.innerHeight - 64}px`});
        window.addEventListener('resize',()=>{
            this.setState({height:`${window.innerHeight-64}px`});
        });
    }

    toToggleRouter=(key)=>{
        console.log(key);
        // this.props.push(`/system/home/${key}`);
    }

    render() {
        return (
            <>
                <Menu theme='dark' mode='inline' className={navStyle.frame} style={{height: this.state.height}} onSelect={this.toToggleRouter}>
                    <SubMenu key='enterprise' title='企业管理' icon={<AppstoreAddOutlined style={{fontSize:'16px'}}/>}>
                        <Menu.Item key='sign' className={navStyle.item}>企业注册</Menu.Item>
                        <Menu.Item key='update' className={navStyle.item}>企业变更</Menu.Item>
                    </SubMenu>
                    <SubMenu key='role' title='角色管理' icon={<UsergroupAddOutlined style={{fontSize:'16px'}}/>}>
                        <Menu.Item key='create' className={navStyle.item}>创建角色</Menu.Item>
                        <Menu.Item key='accredit' className={navStyle.item}>授权管理</Menu.Item>
                    </SubMenu>
                    <SubMenu key='broadcast' title='消息推送' icon={<MailOutlined style={{fontSize:'16px'}}/>}>
                        <Menu.Item key='post' className={navStyle.item}>新建推送</Menu.Item>
                        <Menu.Item key='feedback' className={navStyle.item}>消息管理</Menu.Item>
                    </SubMenu>
                </Menu>
            </>
        );
    }
}

export default withRouter(Nav);
