import React, {PureComponent} from 'react';
import {Button, Input, Space, Checkbox, Modal} from "antd";
import {EyeInvisibleOutlined, EyeTwoTone, UserOutlined, LockOutlined} from '@ant-design/icons';
import loginStyle from './index.module.css';

class LoginPanel extends PureComponent {

    state = {loginLoading: false, codeLoading: false, tip: '获取验证码'};
    toLogin = () => {
        this.setState({loginLoading: true});
        const id = setTimeout(() => {
            Modal.error({
                title:'错误！',
                okText:'确认',
                content:'网络连接超时！请检查您的网络配置，确保可以正常访问网络。',
                afterClose:()=>{
                    this.setState({loginLoading: false});
                }
            });
        }, 1500);//15s后登录失败，弹框提示
        //发送网络请求
    }

    getAuthCode = () => {
        this.setState({codeLoading: true});
        let timer = 60;
        this.setState({tip: `${timer}s后重新获取`});
        const id = setInterval(() => {
            timer = timer - 1;
            if (timer >= 0)
                this.setState({tip: `${timer}s后重新获取`});
            else {
                clearInterval(id);
                this.setState({tip: '获取验证码'});
                this.setState({codeLoading: false});
            }

        }, 1000);
    }

    render() {
        return (
            <div className={loginStyle.login}>
                <h3>用户登录</h3>
                <Space size={40} direction='vertical'>
                    <Input size='large' className={loginStyle.item} placeholder='请输入登录账号' prefix={<UserOutlined/>}/>
                    <Input.Password className={loginStyle.item} size='large' placeholder='请输入登录密码'
                                    prefix={<LockOutlined/>}
                                    iconRender={visible => (visible ? <EyeTwoTone/> : <EyeInvisibleOutlined/>)}/>
                    <div>
                        <Checkbox className={loginStyle.check}>记住登录状态</Checkbox>
                        <Button type='link' className={loginStyle.link}>忘记密码</Button>
                    </div>
                    <Input.Search ref={element => this.auth = element} placeholder='输入验证码' enterButton={this.state.tip}
                                  size='large'
                                  loading={this.state.codeLoading}
                                  onSearch={this.getAuthCode}/>
                    <Button className={loginStyle.item} type='primary'
                            size='large' onClick={this.toLogin}
                            loading={this.state.loginLoading}>登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录</Button>
                </Space>
            </div>
        );
    }
}

export default LoginPanel;
