import React, {Component} from 'react';
import {Form, Input, Steps} from "antd";

import resetStyle from './index.module.css';
import {UserOutlined} from "@ant-design/icons";

const {Step}=Steps;

class ResetPwd extends Component {
    render() {
        return (
            <div className={resetStyle.frame}>
                <h3>找回密码</h3>
                <Steps>
                    <Step title='账号校验' description='输入登录账号'/>
                    <Step title='密码校验' description='请输入密保答案'/>
                    <Step title='设置密码' description='请设置新密码'/>
                </Steps>
                <div className={resetStyle.content}>
                    <Form labelCol={{span:6}} wrapperCol={{span:16}}>
                        <Form.Item label='请输入登录账号' name='uid'>
                            <Input/>
                        </Form.Item>
                        <Form.Item label='请输入验证码' name='code'>
                            <Input/>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        );
    }
}

export default ResetPwd;
