/**
 * 可翻转的卡片
 */
import React, {Component} from 'react';
import tipperStyle from './index.module.css'

class Tipper extends Component {

    overTurnBack=()=>{
        console.log('鼠标事件');
        this.box.style.transform='rotateY(180deg)';
    }

    overTurnFront=()=>{
        console.log('鼠标事件');
        this.box.style.transform='rotateY(-180deg)';
    }

    render() {
        return (
            <div className={tipperStyle.box} ref={element=>{this.box=element}}>
                <div className={tipperStyle.content} onMouseLeave={this.overTurnBack}>
                    <p>{this.props.content}</p>
                </div>
                <div className={tipperStyle.keyInfo} style={{background:this.props.background}} onMouseEnter={this.overTurnFront}>
                    <span>{this.props.title}</span><br/><img src={this.props.icon} alt=''/>
                </div>
            </div>
        );
    }
}

export default Tipper;
