import React, {Component} from 'react';
import * as echarts from 'echarts/core';
import {TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent} from "echarts/components";
import {BarChart} from 'echarts/charts';
import {CanvasRenderer} from "echarts/renderers";

echarts.use([TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent, BarChart, CanvasRenderer]);


class VisitorVolume extends Component {

    componentDidMount() {
        const echart = echarts.init(this.container);
        const option = {
            title: {
                text: '24小时流量统计',
                textStyle: {
                    color: 'white',
                    fontWeight: 'bold',
                    fontsize: 20
                }
            },
            timeline: {
                axisType: 'category',
                data: ['1', '2', '3', '4', '5', '6'],
                autoPlay: true,
                playInterval: '2000',
                realtime: true
            },
            xAxis: [
                {
                    type: 'category',
                    name: '时段',
                    nameLocation: 'end',
                    nameRotate: 30
                }
            ],
            yAxis: [
                {
                    type:'value'
                }
            ],
            series: [
                {
                    type: 'line'
                },
                {
                    type: 'bar'
                }
            ],
            options: [
                {
                    title: {
                        text: '1点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '2点流量统'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '3点流量统'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '4点流量统'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '5点流量统'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '6点流量统'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                }
            ]
        };
        echart.setOption(option);
    }

    render() {
        return (
            <div id='main' ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px', marginTop: '20px'}}/>
        );
    }
}

export default VisitorVolume;
