import React from "react";
import axios from "axios";
import store from "../../redux/store";
import navMemberStyle from "./index.module.css";
import logo from "../../resource/userResource/logoIcon_tencent.png";
import {Avatar, Badge, Button, Space} from "antd";
import head from "../../resource/userResource/headIcon_183098.png";
import {saveUserInfo} from "../../redux/actions/byUserInfo";
import messageIcon from '../../images/messageIcon.png';
import system from '../../images/system.png';
import {NavLink} from "react-router-dom";
import {Redirect, Route, Switch} from "react-router";
import navCommonStyle from "../NavCommon/index.module.css";
import Index from "../../pages/Index";

class NavMember extends React.Component {

    componentDidMount() {
        console.log('成员导航栏挂载完成：', store.getState())
        axios({
            url: 'api/home',
            method: 'post',
            headers: {
                Authorization: store.getState().token
            }
        }).then(response => {
            store.dispatch(saveUserInfo(response.data));
            console.log('用户信息已返回:', store.getState());
        }).catch(err => {

        });
    }

    render() {
        return (
            <>
                <div className={navMemberStyle.header}>
                    <div className={navMemberStyle.left}>
                        <img src={logo}/>
                    </div>
                    <div className={navMemberStyle.right}>
                        <Space size={60}>
                            <Button type='link' style={{padding: 0}} className={navMemberStyle.name}>
                                <Avatar src={head}/>&nbsp;{store.getState().userinfo.userName}
                            </Button>
                            <Badge count={store.getState().userinfo.schedule} size='small'>
                                <Button type='link' style={{padding: 0}}><img src={messageIcon}
                                                                              style={{width: '30px', height: '30px'}}
                                                                              title='未读消息'/></Button>
                            </Badge>
                            <Button type='link' style={{padding: 0}}><img src={system}
                                                                          style={{
                                                                              width: '30px',
                                                                              height: '30px'
                                                                          }}/></Button>
                        </Space>
                    </div>
                    <NavLink to='/index' style={{visible:false}}>首页</NavLink>
                </div>
                <div>
                    <Switch>
                        <Route path='/index' component={Index}/>
                        <Redirect to='/index'/>
                    </Switch>
                </div>
            </>
        );
    }
}

export default NavMember;
