import React, {Component} from 'react';
import Nav from "../Nav";
import Header from "../Header";
import {Redirect, Route, Switch} from "react-router";
import AddMembers from "./AddMembers";
import adminStyle from './index.module.css';

class WorkSpace extends Component {

    state={width:'1700px',height:'1000px'};

    componentDidMount() {
        this.setState({width:`${window.innerWidth-230}px`,heigth:`${window.innerHeight-64}px`});
        window.addEventListener('resize',()=>{
            console.log('监听到窗口缩放事件')
            this.setState({width:`${window.innerWidth-230}px`,heigth:`${window.innerHeight-64}px`});
        });
    }

    render() {
        return (
            <>
                <Header/>
                <Nav/>
                <div className={adminStyle.content} style={{width:this.state.width,height:this.state.height}}>
                    <Switch>
                        <Route path='/admin/workspace/addmembers' compenent={AddMembers}/>
                        <Redirect to='/admin/workspace/addmembers'/>
                    </Switch>
                </div>
            </>
        );
    }
}

export default WorkSpace;
