import React, {Component} from 'react';
import {Table} from "antd";
import infoStyle from './index.module.css';

const {Column}=Table;

class GroupInfo extends Component {

    state={width:'600px'}

    componentDidMount() {
        this.setState({width: `${window.innerWidth - 600}px`});
        window.addEventListener('resize',()=>{
            this.setState({width:`${window.innerWidth-600}px`});
        });
    }

    render() {

        return (
            <div className={infoStyle.table} style={{width:this.state.width}}>
                <Table locale={{emptyText:'请选中组织后操作'}} dataSource={this.props.selected}>
                    <Column title='组织名称' dataIndex='name' key='name' align='center'/>
                    <Column title='上级组织' dataIndex='parent' key='parent' align='center'/>
                    <Column title='成员数量' dataIndex='members' key='members' align='center'/>
                    <Column title='操作' dataIndex='option' key='option' align='center'/>
                </Table>
            </div>
        );
    }
}

export default GroupInfo;
