import React, {PureComponent} from 'react';
import frameStyle from './index.module.css';
import Logo from "../../components/Logo";
import Banner from "../../components/Banner";
import LoginPanel from "../../components/LoginPanel";
import Tipper from "../../components/Tipper";
import terminal from '../../images/terminal.png';
import cost from '../../images/cost.png';
import online from '../../images/online.png';
import flow from '../../images/flow.png';
import safe from '../../images/safe.png';
import {
    THEME_COLOR,
    SECUNDA_COLOR,
    TITLE_ONE,
    TITLE_TWO,
    TITLE_THREE,
    TITLE_FIVE,
    TITLE_FOUR,
    DESCRIBE_FIVE,
    DESCRIBE_FOUR,
    DESCRIBE_ONE,
    DESCRIBE_THREE,
    DESCRIBE_TWO
} from "../../constant";
import Course from "../../components/Course";

class IndexFrame extends PureComponent {

    state = {top: 0};

    render() {
        return (
            <div className={frameStyle.frame}>
                <Logo/>
                <div className={frameStyle.panel}>
                    <Banner/>
                    <LoginPanel/>
                </div>
                <div className={frameStyle.lightSopt}>
                    <h3>选择飞云的五大理由</h3>
                    <Tipper background={SECUNDA_COLOR} content={DESCRIBE_ONE} title={TITLE_ONE} icon={flow}/>
                    <Tipper background={THEME_COLOR} content={DESCRIBE_TWO} title={TITLE_TWO} icon={terminal}/>
                    <Tipper background={SECUNDA_COLOR} content={DESCRIBE_THREE} title={TITLE_THREE} icon={online}/>
                    <Tipper background={THEME_COLOR} content={DESCRIBE_FOUR} title={TITLE_FOUR} icon={cost}/>
                    <Tipper background={SECUNDA_COLOR} content={DESCRIBE_FIVE} title={TITLE_FIVE} icon={safe}/>
                </div>
                <div className={frameStyle.course}>
                    <h3>飞云大事件</h3>
                    <div className={frameStyle.scroll}>
                        <div className={frameStyle.scrollPanel} style={{top: `${this.state.top}px`}}>
                            <Course/>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default IndexFrame;
