import React, {Component} from 'react';
import * as echarts from 'echarts/core';
import {BarChart, ScatterChart} from "echarts/charts";

echarts.use([BarChart, ScatterChart]);

class Efficiency extends Component {

    componentDidMount() {
        const myColor = ['#00a381', '#b44c97', '#a2d7dd', '#00a3af', '#33FFCC'];
        const option = {
            title: {
                text: '部门流转效率排名',
                left: 'center',
                top: 15,
                textStyle: {
                    color: 'white'
                }
            },
            grid: {
                left: 70,
                top: '12%',
                right: 50,
                bottom: '8%',
                containLabel: true
            },
            xAxis: [
                {
                    show: false,
                }
            ],
            yAxis: [
                {
                    axisTick: 'none',
                    axisLine: 'none',
                    offset: '27',
                    axisLabel: {
                        textStyle: {
                            color: '#ffffff',
                            fontSize: '16',
                        }
                    },
                    data: ['市场经营部', '销售管理部', '业务支撑中心', '产品研发部', '行政中心']
                },
                {
                    axisTick: 'none',
                    axisLine: 'none',
                    axisLabel: {
                        textStyle: {
                            color: '#ffffff',
                            fontSize: '16',
                        }
                    },
                    data: ['5', '4', '3', '2', '1']
                },
                {
                    nameGap: '50',
                    nameTextStyle: {
                        color: '#ffffff',
                        fontSize: '16',
                    },
                    axisLine: {
                        lineStyle: {
                            color: 'rgba(0,0,0,0)'
                        }
                    },
                    data: [],
                }
            ],
            series: [
                {
                    name: '流转效率：次/小时',
                    type: 'bar',
                    yAxisIndex: 0,
                    data: [44, 50, 52, 60, 72],
                    tooltip:{
                        show:true,
                        trigger:'item',
                        renderMode:'html',
                        formatter:(params, ticket, callback)=>{
                            return `${params.seriesName}:${params.data}`
                        },
                        textStyle:{
                            width:100,
                            height:100
                        }
                    },
                    label: {
                        normal: {
                            show: true,
                            position: 'right',
                            textStyle: {
                                color: '#ffffff',
                                fontSize: '16',
                            }
                        }
                    },
                    barWidth: 12,
                    itemStyle: {
                        normal: {
                            color: function (params) {
                                const num = myColor.length;
                                return myColor[params.dataIndex % num]
                            },
                        }
                    },
                    z: 2
                },
                {
                    type: 'bar',
                    yAxisIndex: 1,
                    barGap: '-100%',
                    data: [99, 99.5, 99.5, 99.5, 99.5],
                    barWidth: 20,
                    itemStyle: {
                        normal: {
                            color: '#0e2147',
                            barBorderRadius: 5,
                        }
                    },
                    z: 1
                },
                {
                    name: '外框',
                    type: 'bar',
                    yAxisIndex: 2,
                    barGap: '-100%',
                    data: [100, 100, 100, 100, 100],
                    barWidth: 24,
                    itemStyle: {
                        normal: {
                            color: function (params) {
                                const num = myColor.length;
                                return myColor[params.dataIndex % num]
                            },
                            barBorderRadius: 5,
                        }
                    },
                    z: 0
                },
                {
                    name: '外圆',
                    type: 'scatter',
                    hoverAnimation: false,
                    data: [0, 0, 0, 0, 0],
                    yAxisIndex: 2,
                    symbolSize: 35,
                    itemStyle: {
                        normal: {
                            color: function (params) {
                                const num = myColor.length;
                                return myColor[params.dataIndex % num]
                            },
                            opacity: 1,
                        }
                    },
                    z: 2
                }
            ]
        };
        echarts.init(this.container).setOption(option);
    }

    render() {
        return (
            <div ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px', position:'absolute',left:'1524px',top:'96px'}}/>
        );
    }
}

export default Efficiency;
