import React, {Component} from 'react';
import {nanoid} from "nanoid";

class RandomCode extends Component {

    componentDidMount() {
        this.paintCode();
    }

    paintCode=()=>{
        const code=nanoid(4);
        const draw=this.draw.getContext('2d');
        draw.beginPath();
        draw.fillStyle='red';
        draw.moveTo(0,0)
        draw.lineTo(130,10);
        // draw.moveTo(130-Math.round(Math.random()*110),40-Math.round(Math.random()*30));
        // draw.lineTo(130-Math.round(Math.random()*110),40-Math.round(Math.random()*30));
        draw.stroke();
    }

    render() {
        return (
            <canvas style={{width:'130px',height:'40px',background:'#e9e7ef',float:'right'}} ref={elment=>{this.draw=elment}}>
                请使用Google或Edge浏览器登录！
            </canvas>
        );
    }
}

export default RandomCode;
