import React, {Component} from 'react';
import Map, {MapApiLoaderHOC} from 'react-bmapgl/Map';
import {NavigationControl, ZoomControl} from "react-bmapgl";
import {Input} from "antd";

class MapPanel extends Component {
    
    mapTool=React.createRef();

    componentDidMount() {
        const map=this.mapTool.current.map;
        map.loadMapStyleFiles(()=>{
            new window.BMapGL.LocalCity({renderoptions:{map:map}}).get(city=>{
                this.setState({center:city.center})
            });
        });
    }

    location = (value, _) => {
        new window.BMapGL.LocalSearch(this.mapTool.current.map, {
            renderOptions: {map: this.mapTool.current.map, panel: this.panel, pageCapacity: 1}
        }).search(value);
    }

    render() {
        return (
            <div style={{width: '700px', height: '500px'}}>
                <div style={{marginBottom:'10px'}}><Input.Search onSearch={this.location}/></div>
                <Map center={{lng: 116.404449, lat: 39.914889}} zoom={12} ref={this.mapTool} maxZoom={20} style={{width: '700px', height: '500px'}}>
                    <NavigationControl/>
                    <ZoomControl/>
                </Map>
            </div>
        );
    }
}

export default MapApiLoaderHOC({ak: 'mIbTq9DtaAPuV7g7Oy1ZuedzWUGir765'})(MapPanel);
