import React, {Component} from 'react';
import * as echarts from 'echarts/core';
import {TitleComponent, ToolboxComponent, LegendComponent} from 'echarts/components';
import {PieChart} from 'echarts/charts';
import {CanvasRenderer} from 'echarts/renderers';

echarts.use([TitleComponent, ToolboxComponent, LegendComponent, PieChart, CanvasRenderer]);

class Members extends Component {

    componentDidMount() {
        const echart=echarts.init(this.container);
        const option = {
            title: {
                text: '企业成员分布',
                left: 'center'
            },
            legend: {
                top: 'bottom'
            },
            series: [
                {
                    name: '面积模式',
                    type: 'pie',
                    radius: [50, 250],
                    center: ['50%', '50%'],
                    roseType: 'area',
                    itemStyle: {
                        borderRadius: 8
                    },
                    data: [
                        {value: 40, name: '销售人员'},
                        {value: 38, name: '研发人员'},
                        {value: 32, name: '高级科学家'},
                        {value: 30, name: '省级科学家'},
                        {value: 28, name: '长江学者'},
                        {value: 26, name: '中科院士'},
                        {value: 22, name: '管理人员'},
                        {value: 18, name: '生产人员'}
                    ]
                }
            ]
        };
        echart.setOption(option);
    }

    render() {
        return (
            <div id='main' ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px', marginTop: '20px'}}/>
        );
    }
}

export default Members;
