import React, {Component} from 'react';
import {Button, Space, Spin, Table, Tree,Input} from "antd";
import axios from "axios";
import {ApartmentOutlined, DeleteOutlined, EditOutlined, PlusOutlined} from "@ant-design/icons";
import Store from "../../../../redux/store";
import addGroupStyle from './index.module.css';

const {store} = Store;
const {Column} =Table;
const {Search}=Input;

class AddGroups extends Component {

    state={loading:true};

    componentDidMount() {

    }

    render() {
        return (
            <div className={addGroupStyle.container}>
                <div className={addGroupStyle.tree}>
                    <Search placeholder='搜索组织' />
                    <br/>
                    {
                        this.state.loading?<Spin tip='正在获取组织信息，请稍候......'/>:<div></div>
                    }
                </div>
                <div className={addGroupStyle.info}>
                    <Table locale={{emptyText:'请选中具体组织后进行操作！'}} pagination={{hideOnSinglePage:true}}>
                        <Column title='组织名称' dataIndex='groupName' key='groupName' align='center' width='25%'/>
                        <Column title='上级组织' dataIndex='parent' key='parent' align='center' width='25%'/>
                        <Column title='成员数量' dataIndex='members' key='members' align='center' width='25%'/>
                        <Column title='操作' dataIndex='optin' key='option' align='center' width='25%' render={(text, record)=>{
                            console.log(text,record);
                            return (
                                <Space size={15}>
                                    <Button type='link' prefix={<EditOutlined />} title='编辑组织'/>
                                    <Button type='link' prefix={<PlusOutlined />} title='增加下级组织'/>
                                    <Button type='link' prefix={<DeleteOutlined />} title='删除组织'/>
                                </Space>
                            );
                        }}/>
                    </Table>
                </div>
            </div>
        );
    }
}

export default AddGroups;
