import React, {Component} from 'react';
import {Button, Form, Input, Upload, Modal, Progress} from "antd";
import registerStyle from './index.module.css'
import {UploadOutlined} from "@ant-design/icons";
import axios from "axios";
import {Status} from '../../resource/constant';

const formItemLayout = {
    labelCol: {
        sm: {span: 5}
    },
    wrapperCol: {
        sm: {span: 17}
    }
}

const tailLayout = {
    wrapperCol: {offset: 5, span: 17}
}

class Sign extends Component {

    componentDidMount() {
        this.files = new FormData();
    }

    state = {codeState: {verify: '', message: ''}, EmailState: {verify: '', message: ''}, loading: false, percent: 0};
    promise = '';

    verifyCode = () => ({
        validator(_, value) {
            return new Promise((resolve, reject) => {
                axios({
                    url: '/api/verifyOrgEmail',
                    method: 'post',
                    data: {orgCode: value}
                }).then(response => {
                    if (response.data.state === Status.success) {
                        reject('该企业已注册，不允许重复注册！');
                    } else {
                        resolve();
                    }
                }).catch(err => {
                        reject(err.message);
                    }
                );
            });
        }
    })

    verifyEmail = () => ({
        validator(_, value) {
            return new Promise((resolve, reject) => {
                axios({
                    url: '/api/verifyOrgEmail',
                    method: 'post',
                    data: {orgEmail: value}
                }).then(response => {
                    if (response.data.state === Status.success) {
                        reject('该邮箱已绑定企业，不允许重复绑定！');
                    } else {
                        resolve();
                    }
                }).catch(err => {
                        reject(err.message);
                    }
                );
            });
        }
    })

    readyLicense = file => {
        if (this.files.has('license')) {
            this.files.delete('license');
        }
        this.files.append('license', file,
            `license_${this.formRef.getFieldValue('orgCode')}${file.name.slice(file.name.lastIndexOf('.'))}`);
        return false;
    }

    readyAuthLeter = file => {
        if (!this.files.get('license') || this.files.has('authLeter')) {
            this.files.delete('authLeter');
        }
        this.files.append('authLeter', file,
            `authLeter_${this.formRef.getFieldValue('orgCode')}${file.name.slice(file.name.lastIndexOf('.'))}`);
        return false;
    }

    toRegister = (values) => {
        if (!this.files.get('authLeter')) {
            Modal.error({
                title: '错误！',
                okText: '确认！',
                content: '必须上传营业执照和法人授权函！'
            });
            return;
        }
        const {orgCode, orgName, orgSite, orgAddress, orgEmail} = values;
        this.setState({loading: true});
        axios({
            method: 'post',
            url: '/api/register',
            data: {orgCode, orgName, orgSite, orgAddress, orgEmail}
        }).then(response => {
            if (response.data.state === Status.success) {
                axios({
                    url: '/api/upload',
                    method: 'post',
                    headers: {'Content-Type': 'multipart/form-data'},
                    data: this.files
                }).then(response => {
                    console.log(response.data);
                }).catch(err => {
                    alert(err.message);
                });
            }
        }).catch()
    }

    render() {

        return (
            <div className={registerStyle.frame}>
                <img src={require("../../images/register_background.png").default} className={registerStyle.back}
                     alt=''/>
                <div className={registerStyle.content}>
                    <h2 className={registerStyle.title}>企业用户注册</h2>
                    <Form name='register' scrollToFirstError {...formItemLayout}
                          size='large' ref={element => {
                        this.formRef = element
                    }} validateTrigger={['onBlur']} onFinish={this.toRegister}>
                        <Form.Item name='orgCode' label='组织机构代码'
                                   rules={[{
                                       required: true,
                                       message: '组织机构代码不能为空！',
                                       type: 'string',
                                       pattern: /^[A-Z0-9]+$/,
                                   }]}
                                   hasFeedback>
                            <Input allowClear onBlur={this.verifyOrgCode}/>
                        </Form.Item>
                        <Form.Item name='orgName' label='企业名称' hasFeedback
                                   rules={[{required: true, message: '企业名称不能为空！'}]}>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgSite' label='企业官网'
                                   rules={[{required: true, message: '企业站点不合法！', type: 'url'}]}
                                   hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgAddress' label='企业地址' rules={[{required: true, message: '企业地址不能为空！'}]}
                                   hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgLicense' getValueProps={() => {
                            return 'fileList'
                        }} label='营业执照' required>
                            <Upload maxCount={1} accept='.png,.jpg,.pdf' beforeUpload={this.readyLicense}>
                                <Button icon={<UploadOutlined/>}>上传文件</Button>
                            </Upload>
                        </Form.Item>
                        <Form.Item name='orgAuthLeter' getValueProps={() => {
                            return 'fileList'
                        }} label='法人授权函' required>
                            <Upload maxCount={1} accept='.png,.jpg,.pdf' beforeUpload={this.readyAuthLeter}>
                                <Button icon={<UploadOutlined/>}>上传文件</Button>
                            </Upload>
                        </Form.Item>
                        <Form.Item name='orgEmail' label='管理邮箱' validateFirst
                                   rules={[{required: true, message: '邮箱地址不合法！', type: 'email'}, this.verifyEmail]}
                                   hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='pwd' label='登录密码' rules={[{
                            required: true,
                            message: '密码必须包含大小写字母和数字的组合，长度在8-16位之间',
                            type: 'string',
                            pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,16}$/
                        }]} hasFeedback>
                            <Input.Password placeholder='请输入登录密码' allowClear/>
                        </Form.Item>
                        <Form.Item name='configPwd' label='确认密码' dependencies={['pwd']} hasFeedback rules={[
                            {
                                required: true,
                                message: '请再次输入密码并确保两次密码输入一致！',
                            },
                            ({getFieldValue}) => ({
                                    validator(_, value) {
                                        if (!value || getFieldValue('pwd') === value) {
                                            return Promise.resolve();
                                        }
                                        return Promise.reject('两次输入密码不一致！');
                                    }
                                }
                            )
                        ]}>
                            <Input.Password placeholder='请再次输入登录密码' allowClear/>
                        </Form.Item>
                        <Form.Item {...tailLayout}>
                            <Button type='primary' htmlType='submit' style={{width: '100%'}}
                                    loading={this.state.loading}>提交</Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        );
    }
}

export default Sign;
