import React, {Component} from 'react';
import {Button, Space, Spin, Table, Tree, Input, Modal, Form} from "antd";
import axios from "axios";
import {ApartmentOutlined, DeleteOutlined, EditOutlined, PlusOutlined} from "@ant-design/icons";
import Store from "../../../../redux/store";
import addGroupStyle from './index.module.css';

const {store} = Store;
const {Column} = Table;
const {Search} = Input;

class AddGroups extends Component {

    constructor(props) {
        super(props);
        this.state = {loading: true,option:''};
        axios({
            url: 'http://localhost:3000/api/orgGroup',
            method: 'post',
            headers: {authorization: store.getState().token}
        }).then(response => {
            const {groups} = response.data;
            let treeData = '';

            //加工组织数据
            for (let i = 0; i < groups.length; i++) {
                if (groups[i].parent) {
                    for (let j = i + 1; j < groups.length; j++) {
                        if (groups[j].groupCode === groups[i].parent) {
                            if (!groups[j].children) {
                                groups[j].children = [];
                            }
                            let tmp = '';
                            if (groups[i].children) {
                                tmp = {
                                    title: groups[i].groupName,
                                    key: groups[i].groupCode,
                                    children: groups[i].children
                                }
                            } else {
                                tmp = {
                                    title: groups[i].groupName,
                                    key: groups[i].groupCode
                                }
                            }
                            groups[j].children.push(tmp);
                        }
                    }
                } else {
                    treeData = [{
                        title: groups[i].groupName,
                        key: groups[i].groupCode,
                        children: groups[i].children ? groups[i].children : []
                    }];
                }
            }

            this.setState({loading: false, groups, treeData}, () => {

            });
        }).catch();
    }

    showGroupInfo = (selectedKeys, _) => {
        if (selectedKeys.length===0)
            return;
        const {groups} = this.state;
        let selected = {};
        for (let i = 0; i < groups.length; i++) {
            if (groups[i].groupCode === selectedKeys[0]) {
                selected.groupName = groups[i].groupName;
                selected.members = groups[i].groupMembers;
                selected.key = groups[i].groupCode;
                selected.parent = '--';
                const parent = groups[i].parent;
                for (let j = 0; j < groups.length; j++) {
                    if (groups[j].groupCode === parent) {
                        selected.parent = groups[j].groupName;
                        break;
                    }
                }
                break;
            }

        }
        this.setState({selected});
    }

    render() {
        return (
            <div className={addGroupStyle.container}>
                <div className={addGroupStyle.left}>
                    <Search placeholder='搜索组织'/>
                    <div style={{marginTop: '10px'}}>
                        {
                            this.state.loading ?
                                <Spin tip='正在获取组织信息，请稍候......'/> :
                                <Tree treeData={this.state.treeData} icon={<ApartmentOutlined/>} showIcon={true}
                                      showLine={{showLeafIcon: false}} onSelect={this.showGroupInfo}/>
                        }
                    </div>
                </div>
                <div className={addGroupStyle.info}>
                    <Table locale={{emptyText: '请选中具体组织后进行操作！'}} pagination={{hideOnSinglePage: true}}
                           dataSource={this.state.selected ? [this.state.selected] : []}>
                        <Column title='组织名称' dataIndex='groupName' key='groupName' align='center' width='25%'/>
                        <Column title='上级组织' dataIndex='parent' key='parent' align='center' width='25%'/>
                        <Column title='成员数量' dataIndex='members' key='members' align='center' width='25%'/>
                        <Column title='操作' dataIndex='option' align='center' width='25%'
                                render={(_, record) => (
                                    <Space size={10}>
                                        <Button type='link' icon={<EditOutlined/>} title='编辑组织' onClick={()=>{this.setState({option:'edit'})}}/>
                                        <Button type='link' icon={<PlusOutlined/>} title='增加下级组织'/>
                                        <Button type='link' icon={<DeleteOutlined/>} title='删除组织'/>
                                    </Space>
                                )}/>
                    </Table>
                </div>
                <Modal title='编辑组织' maskClosable={false} visible={this.state.option==='edit'} onCancel={()=>{this.setState({option:''})}}>
                    <Form>
                        <Form.Item label='组织名称：' initialValue='123'>
                            <Input/>
                        </Form.Item>
                    </Form>
                </Modal>
            </div>
        );
    }
}

export default AddGroups;
