import React, {Component} from 'react';
import {nanoid} from "nanoid";

class RandomCode extends Component {

    componentDidMount() {
        this.paintCode();
    }

    paintCode=()=>{
        const code=nanoid(4);
        const draw=this.draw.getContext('2d');
        draw.fillRect(20,20,100,100);
        draw.moveTo(130-Math.round(Math.random()*130),40-Math.round(Math.random()*40));
        draw.strokeStyle='red';
        draw.lineTo(130-Math.round(Math.random()*130),40-Math.round(Math.random()*40));
    }

    render() {
        return (
            <canvas style={{width:'130px',height:'40px',background:'#e9e7ef',float:'right'}} ref={elment=>{this.draw=elment}}>

            </canvas>
        );
    }
}

export default RandomCode;
