import {OPTIONS} from "../../resource/constant";

const initInfo='';

export default function userInfo(preState=initInfo,action){
    const {type,data}=action;
    switch (type) {
        case OPTIONS.save:
            return preState=data;
        default:
            return preState='';
    }
}
