import React, {Component} from 'react';
import {Button, Form, Input,Upload} from "antd";
import registerStyle from './index.module.css'
import {UploadOutlined} from "@ant-design/icons";
import axios from "axios";
import {Status} from '../../resource/constant';

const formItemLayout = {
    labelCol: {
        xs: {span: 24},
        sm: {span: 5},
    },
    wrapperCol: {
        xs: {span: 24},
        sm: {span: 16},
    },
}

class Sign extends Component {

    state = {codeState: {verify: '', message: ''},nameState:{verify: '', message: ''}};

    verifyOrgCode = () => {
        const code = this.formRef.getFieldValue('orgCode');
        if (!code) {
            this.setState({codeState: {verify: Status.error, helpMsg: '组织机构代码不能为空！'}});
            return;
        }
        this.setState({codeState: {verify: Status.verifying, helpMsg: ''}});
        axios({
            url: '/api/verifyOrgCode',
            method: 'post',
            data: {orgCode: code}
        }).then(response => {
            if (response.data.state === Status.success) {
                this.setState({codeState: {verify: Status.error, helpMsg: '该企业已注册，不允许重复注册！'}});
            } else {
                this.setState({codeState: {verify: Status.success, helpMsg: ''}});
            }
        }).catch(err => {
            alert(err.message);
        });
    }

    verifyName=()=>{
        const name = this.formRef.getFieldValue('orgName');
        if (!code) {
            this.setState({nameState: {verify: Status.error, helpMsg: '企业名称不能为空！'}});
            return;
        }else {
            this.setState({nameState: {verify: Status.success, helpMsg: ''}});
        }
    }

    render() {
        return (
            <div className={registerStyle.frame}>
                <img src={require("../../images/register_background.png").default} className={registerStyle.back} alt=''/>
                <div className={registerStyle.content}>
                    <h2 className={registerStyle.title}>企业用户注册</h2>
                    <Form name='register' scrollToFirstError {...formItemLayout}
                          size='large' ref={element=>{this.formRef = element}}>
                        <Form.Item name='orgCode' label='组织机构代码' validateStatus={this.state.codeState.verify}
                                   hasFeedback help={this.state.codeState.helpMsg}>
                            <Input allowClear onBlur={this.verifyOrgCode}/>
                        </Form.Item>
                        <Form.Item name='orgName' label='企业名称' validateStatus={this.state.nameState.verify} hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgSite' label='企业官网'
                                   rules={[{required: true, message: '企业站点不合法！', type: 'url'}]}
                                   hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgAddress' label='企业地址'>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='orgLicense' label='营业执照'>
                            <Upload maxCount={1} action='' accept='.png,.jpg,.pdf'>
                                <Button icon={<UploadOutlined/>}>上传文件</Button>
                            </Upload>
                        </Form.Item>
                        <Form.Item name='orgAuthLeter' label='法人授权函'>
                            <Upload maxCount={1} action='' accept='.png,.jpg,.pdf'>
                                <Button icon={<UploadOutlined/>}>上传文件</Button>
                            </Upload>
                        </Form.Item>
                        <Form.Item name='orgEmail' label='管理邮箱'
                                   rules={[{required: true, message: '邮箱地址不合法！', type: 'email'}]}
                                   validateStatus='success' hasFeedback>
                            <Input allowClear/>
                        </Form.Item>
                        <Form.Item name='pwd' label='登录密码'>
                            <Input.Password placeholder='请输入登录密码' allowClear/>
                        </Form.Item>
                        <Form.Item name='configPwd' label='确认密码'>
                            <Input.Password placeholder='请再次输入登录密码' allowClear/>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        );
    }
}

export default Sign;
