import React, {Component} from 'react';
import {Tree} from "antd";
import axios from "axios";
import store from "../../../../redux/store";

class AddGroups extends Component {

    state = {list: ''};

    transData = (data) => {
        const treeData={
            title:'',
            key:'',
            children:[]
        };
        data.forEach((item, _, array) => {
            if (item.parent) {
                for (let tmp of array) {
                    if (tmp.groupCode === item.parent) {
                        if (!tmp.children) {
                            tmp.children = [];
                        }
                        if (!tmp.title){
                            tmp.title=tmp.groupName;
                        }
                        if (!tmp.key){
                            tmp.key=tmp.groupCode;
                        }
                        tmp.nodes.push(item);
                    }
                }
            }
        })
        return data.filter(item =>
            !item.parent
        );
    }

    componentDidMount() {
        axios({
            url: 'http://localhost:3000/api/orgGroup',
            method: 'post',
            headers: {Authorization: store.getState().token}
        }).then(response => {
            const groups =this.transData(response.data.groups);
            this.setState({list:this.transData(groups)});
        }).catch();
    }

    render() {
        return (
            <Tree treeData={this.state.list}/>
        );
    }
}

export default AddGroups;
