import React, {Component} from 'react';
import * as echarts from 'echarts/core';
import {TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent} from "echarts/components";
import {BarChart,LineChart} from 'echarts/charts';
import {CanvasRenderer} from "echarts/renderers";

echarts.use([TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent,LineChart, BarChart, CanvasRenderer]);


class VisitorVolume extends Component {

    componentDidMount() {
        const echart = echarts.init(this.container);
        const theOption = {
            title: {
                text: '24小时流量统计',
                left:'center',
                top:15,
                textStyle: {
                    color: 'white',
                    fontWeight: 'bold',
                    fontsize: 20
                }
            },
            tooltip:{
                trigger:'axis',
                show:true,
                formatter:(params)=>{
                    alert(params);
                    return `${params.name} ${params.seriesName} ${params.data}人`
                }
            },
            timeline: {
                axisType: 'category',
                data: ['1', '2', '3', '4', '5', '6'],
                autoPlay: true,
                playInterval: '2000',
                realtime: true,
                bottom: 10
            },
            legend:{
                right:20,
                top:30,
                data:['净增访问量','累计访问量'],
                textStyle:{
                    color:'white'
                }
            },
            xAxis: [
                {
                    type: 'category',
                    name: '时段',
                    nameLocation: 'end',
                    data: ['1', '2', '3', '4', '5', '6'],
                    nameTextStyle:{
                        color: 'white'
                    }
                }
            ],
            yAxis: [
                {
                    type: 'value',
                    name: '净增访问量',
                    axisLabel: {
                        formatter: '{value} 人次'
                    }
                },
                {
                    type: 'value',
                    name: '累计访问量',
                    axisLabel: {
                        formatter: '{value} 人次'
                    }
                }
            ],
            grid:{
                left:100,
                bottom:100,
                top:100
            },
            textStyle: {
                color:'white'
            },
            series: [
                {
                    name: '净增访问量',
                    type: 'line',
                    legendHoverLink:true,
                    lineStyle:{
                        color:'green',
                        width:2
                    },
                    // tooltip:{
                    //     show: true,
                    //     formatter:(params)=>{
                    //         return `${params.name}点 ${params.seriesName} ${params.data}人`
                    //     }
                    // }
                },
                {
                    name: '累计访问量',
                    type: 'bar',
                    legendHoverLink:true,
                    itemStyle:{
                        color:'rgba(16,171,227,0.9)'
                    }
                }
            ],
            options: [
                {
                    title: {
                        text: '1点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '2点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '3点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '4点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '5点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '6点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                }
            ]
        };
        theOption && echart.setOption(theOption);
    }

    render() {
        return (
            <div id='main' ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px',position:'absolute',top:'96px',left:'12px'}}/>
        );
    }
}

export default VisitorVolume;
