import React, {PureComponent} from 'react';
import {Button, Input, Space, Checkbox, Modal, PageHeader, Spin, Result} from "antd";
import {EyeInvisibleOutlined, EyeTwoTone, UserOutlined, LockOutlined, ScanOutlined} from '@ant-design/icons';
import loginStyle from './index.module.css';
import code from '../../images/code.png';

class LoginPanel extends PureComponent {

    state = {
        loginLoading: false,
        codeLoading: false,
        tip: '获取验证码',
        position: {top: '500px', left: '400px'},
        codeState: 'waiting'
    };
    /**
     * 校验表单填写情况后向服务器发送登录请求
     */
    toLogin = () => {
        if (!this.uid.state.value || !this.pwd.state.value || !this.auth.state.value) {
            Modal.error({
                title: '错误！',
                okText: '确认',
                content: '登录账号及密码均不能为空，并请输入验证码！'
            });
        } else {
            this.setState({loginLoading: true});
            const id = setTimeout(() => {
                Modal.error({
                    title: '错误！',
                    okText: '确认',
                    content: '网络连接超时！请检查您的网络配置，确保可以正常访问网络。',
                    afterClose: () => {
                        this.setState({loginLoading: false});
                    }
                });
            }, 15000);//15s后登录失败，弹框提示

            //发送网络请求
        }
    }

    /**
     * 拉取验证码
     */

    getAuthCode = () => {
        this.setState({codeLoading: true});
        let timer = 60;
        this.setState({tip: `${timer}s后重新获取`});
        const id = setInterval(() => {
            timer = timer - 1;
            if (timer >= 0)
                this.setState({tip: `${timer}s后重新获取`});
            else {
                clearInterval(id);
                this.setState({tip: '获取验证码'});
                this.setState({codeLoading: false});
            }

        }, 1000);
    }

    /**
     * 拉取登录二维码
     */
    showCode = () => {
        this.setState({position: {top: '0px', left: '0px'}});
        const id = setTimeout(() => {
            this.setState({codeState: false})
        }, 15000);
    }

    /**
     * 隐藏二维码
     */
    hiddenCode = () => {
        this.setState({position: {top: '500px', left: '400px'}});
    }

    render() {
        return (
            <div className={loginStyle.login}>
                <form className={loginStyle.form}>
                    <h3>用户登录</h3>
                    <Space size={32} direction='vertical'>
                        <Input type='text' ref={element => this.uid = element} size='large' name='userId'
                               className={loginStyle.item}
                               placeholder='请输入登录账号' prefix={<UserOutlined/>}/>
                        <Input.Password ref={element => this.pwd = element} name='userPwd' className={loginStyle.item}
                                        size='large' placeholder='请输入登录密码'
                                        prefix={<LockOutlined/>}
                                        iconRender={visible => (visible ? <EyeTwoTone/> : <EyeInvisibleOutlined/>)}/>
                        <div>
                            <Checkbox className={loginStyle.check}>记住登录状态</Checkbox>
                            <Button type='link' className={loginStyle.link}>忘记密码</Button>
                        </div>
                        <Input.Search ref={element => this.auth = element} placeholder='输入验证码'
                                      enterButton={this.state.tip}
                                      size='large'
                                      loading={this.state.codeLoading}
                                      onSearch={this.getAuthCode}/>
                        <Button className={loginStyle.item} type='primary'
                                size='large' onClick={this.toLogin}
                                loading={this.state.loginLoading}>登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录</Button>
                        <ScanOutlined className={loginStyle.scan} title='二维码登录' onClick={this.showCode}/>
                    </Space>
                </form>
                <div className={loginStyle.code} style={{
                    top: this.state.position.top,
                    left: this.state.position.left,
                }}>
                    <PageHeader title='返回' onBack={this.hiddenCode}/>
                    <br/>
                    {
                        this.state.codeState === 'waiting' ?
                            <div><Spin size='large'/><br/><br/><span>正在获取登录二维码，请稍候...</span></div> :
                            this.state.codeState ?
                                <div><img src={code} alert=''/><br/><br/><p>请打开App并扫描二维码登录</p></div> :
                                <div>
                                    <Result status='error'
                                            title='错误！'
                                            subTitle='获取登录二维码失败！请检查网络设置或尝试重新获取。'
                                            extra={[
                                                <Button type="primary" key='again' onClick={this.showCode}>重试</Button>
                                            ]}
                                    >
                                    </Result>
                                </div>
                    }
                </div>
            </div>
        );
    }

}

export default LoginPanel;
