import React, {Component} from 'react';

class Members extends Component {

    componentDidMount() {
        const option = {
            title: {
                text: '企业成员分布',
                left: 'center'
            },
            legend: {
                top: 'bottom'
            },
            toolbox: {
                show: true,
                feature: {
                    mark: {show: true},
                    dataView: {show: true, readOnly: false},
                    restore: {show: true},
                    saveAsImage: {show: true}
                }
            },
            series: [
                {
                    name: '面积模式',
                    type: 'pie',
                    radius: [50, 250],
                    center: ['50%', '50%'],
                    roseType: 'area',
                    itemStyle: {
                        borderRadius: 8
                    },
                    data: [
                        {value: 40, name: '销售人员'},
                        {value: 38, name: '研发人员'},
                        {value: 32, name: '高级科学家'},
                        {value: 30, name: '省级科学家'},
                        {value: 28, name: '长江学者'},
                        {value: 26, name: '中科院士'},
                        {value: 22, name: '管理人员'},
                        {value: 18, name: '生产人员'}
                    ]
                }
            ]
        };
    }

    render() {
        return (
            <div>

            </div>
        );
    }
}

export default Members;
