import React, {Component} from 'react';
import {Button, Form, Input, Steps} from "antd";

import resetStyle from './index.module.css';
import {UserOutlined} from "@ant-design/icons";
import RandomCode from "../../../components/RandomCode";

const {Step}=Steps;

class ResetPwd extends Component {
    render() {
        return (
            <div className={resetStyle.frame}>
                <h3>找回密码</h3>
                <Steps>
                    <Step title='账号校验' description='输入登录账号'/>
                    <Step title='密码校验' description='请输入密保答案'/>
                    <Step title='设置密码' description='请设置新密码'/>
                </Steps>
                <div className={resetStyle.content}>
                    <Form labelCol={{span:6}} wrapperCol={{span:14}}>
                        <Form.Item label='请输入账号' name='uid'>
                            <Input/>

                        </Form.Item>
                        <Form.Item label='请输入验证码'>
                            <Form.Item name='code' style={{display:'inline-block',width:'330px',float:'left'}}>
                                <Input/>
                            </Form.Item>
                            <Form.Item style={{display:'inline-block',width:'130px',float:'right'}}>
                                <RandomCode height={30}/>
                            </Form.Item>
                        </Form.Item>
                        <Form.Item wrapperCol={{offset:6,span:14}}>
                            <Button type='primary' block>下&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;步</Button>
                        </Form.Item>
                    </Form>
                </div>
            </div>
        );
    }
}

export default ResetPwd;
