import React, {Component} from 'react';
import {Table, InputNumber, Space} from "antd";

class PriceCard extends Component {

    add = () => {
    };

    render() {

        const columns = [
            {title: '套餐', dataIndex: 'combo', align: 'center'},
            {title: '内容', dataIndex: 'detail', align: 'center'},
            {title: '价格', dataIndex: 'price', align: 'center'},
            {
                title: '数量', key: 'action', align: "center", render: (text, record) => (
                    <Space size="middle">
                        <a>Invite</a>
                        <a>Delete</a>
                    </Space>)
            }
        ];

        const rowSelection = {
            type: 'checkbox'
        };

        // const data = [
        //     {key: '001', combo: '桌面OA服务', detail: '默认1000个桌面账号终身授权', price: '¥0.00'},
        //     {key: '002', combo: '移动App服务', detail: '默认1000个桌面账号终身授权', price: '¥0.00'},
        //     {key: '003', combo: '桌面License', detail: '增加1000个账号终身授权', price: '¥100,000.00'},
        //     {key: '004', combo: '移动License', detail: '增加1000个账号终身授权', price: '¥100,000.00'}
        // ];

        return (
            <Table rowSelection={rowSelection} columns={columns} dataSource={this.props.dataSource} pagination={false}/>
        );
    }
}

export default PriceCard;
