/**
 * 可翻转的卡片
 */
import React, {Component} from 'react';
import tipperStyle from './index.module.css'

class Tipper extends Component {

    componentDidMount() {
        this.box.addEventListener('mouseenter',(event)=>{
            this.box.style.transform='rotateY(180deg)';
        });
        this.box.addEventListener('mouseleave',(event)=>{
            event.stopPropagation();
            this.box.style.transform='rotateY(0deg)';
        })
    }

    render() {
        return (
            <div className={tipperStyle.box} ref={element=>{this.box=element}}>
                <div className={tipperStyle.content}>
                    <p>{this.props.content}</p>
                </div>
                <div className={tipperStyle.keyInfo} style={{background:this.props.background}}>
                    <span>{this.props.title}</span><br/><img src={this.props.icon} alt=''/>
                </div>
            </div>
        );
    }
}

export default Tipper;
