import React, {Component} from 'react';
import {Tree} from "antd";
import axios from "axios";
import store from "../../../../redux/store";

class AddGroups extends Component {

    state = {list: ''};

    transData = (data) => {
        data.forEach((item, _, array) => {
            if (item.parent) {
                for (let tmp of array) {
                    if (tmp.groupCode === item.parent) {
                        if (!tmp.nodes) {
                            tmp.nodes = [];
                        }
                        tmp.nodes.push(item);
                    }
                }
            }
        })
        return data.filter(item =>
            !item.parent
        );
    }

    componentDidMount() {
        axios({
            url: 'http://localhost:3000/api/orgGroup',
            method: 'post',
            headers: {Authorization: store.getState().token}
        }).then(response => {
            const grous =this.transData(response.data.groups);
        }).catch();
    }

    render() {
        return (
            <Tree loadData={this.state.list}/>
        );
    }
}

export default AddGroups;
