import React, {PureComponent} from 'react';
import {Form, Input, Tabs, Checkbox, Button} from "antd";
import {withRouter} from "react-router";

import loginStyle from './index.module.css';
import {LockOutlined, UserOutlined, VerifiedOutlined} from "@ant-design/icons";
import RandomCode from "../RandomCode";

const {TabPane}=Tabs;

class Login extends PureComponent {

    render() {
        return (
            <div className={loginStyle.login}>
                <Tabs type='card' size='large' animated={{inkBar:true,tabPane:true}}>
                    <TabPane tab='企业成员登录' key='member'>
                        <Form wrapperCol={{span:24}} style={{marginTop:'10px'}}>
                            <Form.Item name='identity' hasFeedback rules={[
                                {
                                    required:true,
                                    type:'string',
                                    pattern:/^[A-Za-z0-9]{3,8}$/,
                                    message:'企业标识为3~8位数字或字母'
                                }
                            ]}>
                                <Input prefix={<VerifiedOutlined />} placeholder='请输入企业唯一标识'/>
                            </Form.Item>
                            <Form.Item name='uid'>
                                <Input prefix={<UserOutlined />} placeholder='请输入登录账号' autoComplete='username' rules={[
                                    {
                                        required:true,
                                        type:'string',
                                        pattern:/^[A-Za-z0-9]{3,8}$/,
                                        message:'登录账号为8~16位数字或字母'
                                    }
                                ]}/>
                            </Form.Item>
                            <Form.Item name='pwd'>
                                <Input.Password prefix={<LockOutlined />} placeholder='请输入登录密码' autoComplete='current-password'/>
                            </Form.Item>
                            <Form.Item>
                                <div style={{float:'left'}}>
                                    <Checkbox>记住密码</Checkbox>
                                </div>
                                <div style={{float:'right'}}>
                                    <Checkbox>自动登录</Checkbox>
                                </div>
                            </Form.Item>
                            <Form.Item>
                                <Input placeholder='请输入验证码' style={{width:'205px'}}/>
                                <RandomCode height={30}/>
                            </Form.Item>
                            <Form.Item>
                                <Button type='primary' block>登录</Button>
                            </Form.Item>
                        </Form>
                    </TabPane>
                    <TabPane tab='企业管理员登录' key='admin'>

                    </TabPane>
                </Tabs>
                <div>
                    <Button type='link'>找回密码</Button>
                </div>
            </div>
        );
    }

}

export default withRouter(Login);
