import React, {Component} from 'react';
import echarts from 'echarts/lib/echarts';
import  'echarts/lib/chart/bar';
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';


class VisitorVolume extends Component {

    componentDidMount() {
        const myChart = echarts.init(this.container);
        myChart.setOption({
            title: { text: '24小时流量统计' ,left:'ceter'},
            tooltip: {},
            xAxis: {
                data: ["衬衫", "羊毛衫", "雪纺衫", "裤子", "高跟鞋", "袜子"]
            },
            yAxis: {},
            series: [{
                name: '销量',
                type: 'bar',
                data: [5, 20, 36, 10, 10, 20]
            }]
        });
    }

    render() {
        return (
            <div id='main' ref={element=>{this.container=element}} style={{width:'630px',height:'428px',marginTop:'20px'}}/>
        );
    }
}

export default VisitorVolume;
