import React, {Component} from 'react';
import {Tree} from "antd";
import axios from "axios";
import {ApartmentOutlined} from "@ant-design/icons";
import Store from "../../../../redux/store";
import addGroupStyle from './index.module.css';
import GroupInfo from "./GroupInfo";

const {store}=Store;


class AddGroups extends Component {

    state = {list: '',height:'1000px',selected:[],source:''};

    transData = (data) => {
        const treeData={
            title:'',
            key:'',
            children:[]
        };
        data.forEach((item, _, array) => {
            if (item.parent) {
                for (let tmp of array) {
                    if (tmp.groupCode === item.parent) {
                        if (!tmp.children) {
                            tmp.children = [];
                        }
                        if (!tmp.title){
                            tmp.title=tmp.groupName;
                        }
                        if (!tmp.key){
                            tmp.key=tmp.groupCode;
                        }
                        item.title=item.groupName;
                        item.key=item.groupCode;
                        // item.icon=<ApartmentOutlined/>
                        // delete item.groupName;
                        // delete item.groupCode;
                        tmp.children.push(item);
                    }
                }
            }else {
                treeData.title= item.groupName;
                treeData.key=item.groupCode;
                treeData.children=item.children;
            }
        })
        return treeData;
    }

    getGroupInfo=(selectedKeys, e)=>{
        let parent='';
        const groupInfo=this.state.source.filter(item=>{
            if (item.groupCode===selectedKeys[0]){
                parent=item.parent;
                return item;
            }
        });
        const parentInfo=this.state.source.filter(item=>{
            if (item.groupCode===parent){
                return item;
            }
        });

        const selected={name:groupInfo[0].groupName,parent:parentInfo[0].groupName,members:groupInfo[0].groupMembers,key:groupInfo[0].groupCode};
        console.log(selected);
        this.setState({selected:[selected]});
    }

    componentDidMount() {
        this.setState({height: `${window.innerHeight - 74}px`});
        window.addEventListener('resize',()=>{
            this.setState({height:`${window.innerHeight-74}px`});
        });
        axios({
            url: 'http://localhost:3000/api/orgGroup',
            method: 'post',
            headers: {Authorization: store.getState().token}
        }).then(response => {
            const source=response.data.groups;
            this.setState({source:source});
            const groups =this.transData(source);
            this.setState({list:[groups]});
        }).catch();
    }

    render() {
        return (
            <>
                <div className={addGroupStyle.group} style={{height:this.state.height}}>
                    <Tree treeData={this.state.list} icon={<ApartmentOutlined/>} showIcon={true} showLine={{showLeafIcon:false}} onSelect={this.getGroupInfo}/>
                </div>
                <GroupInfo selected={this.state.selected}/>
            </>

        );
    }
}

export default AddGroups;
