import React, {Component} from 'react';
import {nanoid} from "nanoid";

class RandomCode extends Component {

    componentDidMount() {
        this.paintCode();
    }

    paintCode=()=>{
        const code=nanoid(4);
        const draw=this.draw.getContext('2d');
        draw.clearRect(0,0,130,40);
        draw.beginPath();
        draw.fillStyle='darkgray';
        draw.moveTo(Math.round(Math.random()*20),Math.round(Math.random()*40));
        draw.lineTo(Math.round(130-Math.random()*20),Math.round(Math.random()*40));
        draw.stroke();
        draw.closePath();
        draw.fillStyle='red';
        draw.font='18px 微软雅黑'
        draw.fillText(code,10,25,100);
    }

    render() {
        return (
            <canvas width='130' height='40' style={{background:'#e9e7ef',float:'right'}} ref={elment=>{this.draw=elment}} onClick={this.paintCode}>
                请使用Google或Edge浏览器登录！
            </canvas>
        );
    }
}

export default RandomCode;
