import React, {Component} from 'react';
import {Avatar, Button, Form, Input, Modal, Select} from "antd";
import {DesktopOutlined, SettingOutlined} from "@ant-design/icons";
import axios from "axios";


import navStyle from './index.module.css';
import Store from '../../../../redux/store';

const {Option} = Select;
const {store} = Store;

class Nav extends Component {

    constructor(props) {
        super(props);
        // console.log(store.getState());
        // const {init}=store.getState().userinfo;
        this.state = ({visible: false, questions: [], selects: []});
    }

    componentDidMount() {
        const {init} = store.getState().userinfo;
        if (!init) {
            axios({
                url: 'http://localhost:3000/api/questions',
                method: 'get'
            }).then(response => {
                console.log(response.data);
                this.setState({visible: true, questions: response.data});
            }).catch(() => {

            });
        }
    }

    toSelect=(value)=>{
        this.state.selects.push(value);
        this.setState({},()=>{
            console.log(this.state);
        });
    }

    toClear=()=>{

    }

    render() {

        return (
            <div className={navStyle.frame}>
                <div className={navStyle.left}>
                    <img src={require('../../../../images/logoH.png').default} alt=''/>
                </div>
                <div className={navStyle.right}>
                    <div className={navStyle.user}>
                        <Avatar src={require('../../../../images/admin.png').default} alt='资源获取失败'
                                className={navStyle.icon}/>
                        <span className={navStyle.id}>{store.getState().userinfo.uid}</span>
                        <SettingOutlined title='快速设置' className={navStyle.set}/>
                        <DesktopOutlined title='返回主页' className={navStyle.set}/>
                    </div>
                </div>
                <Modal visible={this.state.visible} title='修改密码' footer={null} closable={false}>
                    <Form labelCol={{span: 5}} wrapperCol={{span: 17}}>
                        <Form.Item label='请输入密码' name='pwd'>
                            <Input.Password/>
                        </Form.Item>
                        <Form.Item label='请确认密码' name='configPwd'>
                            <Input.Password/>
                        </Form.Item>
                        <Form.Item label='密保问题' name='qestion1'>
                            <Select value={this.state.selects[0]?this.state.selects[0]:''} onSelect={this.toSelect} allowClear onClear={this.toClear}>
                                {
                                    this.state.questions.map((item, index) => {
                                        if (!this.state.selects.includes(item.question)){
                                            return (<Option value={item.question} key={index}>{item.question}</Option>);
                                        }else {
                                            return null;
                                        }
                                    })
                                }
                            </Select>
                        </Form.Item>
                        <Form.Item label='输入答案' name='answer1'>
                            <Input/>
                        </Form.Item>
                        <Form.Item label='密保问题' name='qestion2' >
                            <Select value={this.state.selects[1]?this.state.selects[1]:''}  onSelect={this.toSelect} allowClear onClear={this.toClear}>
                                {
                                    this.state.questions.map((item, index) => {
                                        if (!this.state.selects.includes(item.question)){
                                            return (<Option value={item.question} key={index}>{item.question}</Option>);
                                        }else {
                                            return null;
                                        }
                                    })
                                }
                            </Select>
                        </Form.Item>
                        <Form.Item label='输入答案' name='answer2'>
                            <Input/>
                        </Form.Item>
                        <Form.Item label='密保问题' name='qestion3'>
                            <Select value={this.state.selects[2]?this.state.selects[2]:''}  onSelect={this.toSelect} allowClear onClear={this.toClear}>
                                {
                                    this.state.questions.map((item, index) => {
                                        if (!this.state.selects.includes(item.question)){
                                            return (<Option value={item.question} key={index}>{item.question}</Option>);
                                        }else {
                                            return null;
                                        }
                                    })
                                }
                            </Select>
                        </Form.Item>
                        <Form.Item label='输入答案' name='answer3'>
                            <Input/>
                        </Form.Item>
                        <Form.Item wrapperCol={{offset: 5, span: 17}}>
                            <Button type='primary' htmlType='submit'
                                    block>提&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;交</Button>
                        </Form.Item>
                    </Form>
                </Modal>
            </div>

        );
    }
}

export default Nav;
