import React, {Component} from 'react';
import Map, {MapApiLoaderHOC} from 'react-bmapgl/Map';
import {NavigationControl, ZoomControl} from "react-bmapgl";
import {Input} from "antd";

class MapPanel extends Component {

    state = {center: {lng: 116.404449, lat: 39.914889}};

    componentDidMount() {
        console.log(this.map);
        this.map.loadMapStyleFiles(() => {
            new window.BMapGL.LocalCity({renderOptions: {map: this.map}}).get((city) => {
                this.setState({center: city.center});
            });
            const cityControl = new window.BMapGL.CityListControl({
                anchor: window.BMapGL.BMAP_ANCHOR_TOP_RIGHT
            });
            this.map.addControl(cityControl);
            const locationControl = new window.BMapGL.LocationControl({
                anchor: window.BMapGL.BMAP_ANCHOR_TOP_RIGHT,
            });
            this.map.addControl(locationControl);
        });
    }

    location = (value, _) => {
        new window.BMapGL.LocalSearch(this.map, {
            renderOptions: {map: this.map, panel: this.panel, pageCapacity: 1}
        }).search(value);
    }

    render() {
        return (
            <div style={{width: '700px', height: '500px'}}>
                <div><Input.Search onSearch={this.location}/></div>
                <Map center={this.state.center} ref={element => {
                    this.map = element
                }} maxZoom={20} style={{width: '700px', height: '500px'}}>
                    <NavigationControl/>
                    <ZoomControl/>
                </Map>
            </div>
        );
    }
}

export default MapApiLoaderHOC({ak: 'mIbTq9DtaAPuV7g7Oy1ZuedzWUGir765'})(MapPanel);
