/**
 * 可翻转的卡片
 */
import React, {Component} from 'react';
import tipperStyle from './index.module.css'

class Tipper extends Component {

    state={overturn:false};

    overTrun=()=>{
        this.setState((state)=> ({overturn: !state.overturn}))
    }

    render() {
        return (
            <div className={tipperStyle.box} onMouseOver={this.overTrun} onMouseOut={this.overTrun}>
                <div className={tipperStyle.content} style={{transform:`rotateY(${this.state.overturn?'180deg':'0deg'})`}}>
                    <p>{this.props.content}</p>
                </div>
                <div className={tipperStyle.keyInfo} style={{background:this.props.background}}>
                    <span>{this.props.title}</span><br/><img src={this.props.icon} alt=''/>
                </div>
            </div>
        );
    }
}

export default Tipper;
