import React, {Component} from 'react';
import * as echarts from 'echarts/core';
import {TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent} from "echarts/components";
import {BarChart,LineChart} from 'echarts/charts';
import {CanvasRenderer} from "echarts/renderers";

echarts.use([TimelineComponent, TitleComponent, TooltipComponent, GridComponent, LegendComponent,LineChart, BarChart, CanvasRenderer]);


class VisitorVolume extends Component {

    componentDidMount() {
        const echart = echarts.init(this.container);
        const theOption = {
            // title: {
            //     text: '请求数',
            //     textStyle: {
            //         fontWeight: 'normal',
            //         fontSize: 16,
            //         color: '#F1F1F3',
            //     },
            //     left: '6%',
            // },
            timeline: {
                axisType: 'category',
                data: ['1:00', '2:00', '3:00', '4:00', '5:00', '6:00'],
                autoPlay: true,
                playInterval: '2000',
                realtime: true,
                bottom: 10,
                left: 100,
                right: 100
            },
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    lineStyle: {
                        color: '#57617B',
                    },
                },
            },
            grid: {
                left: 100,
                right: 100,
                bottom: 80,
                top:80,
                // containLabel: true,
            },
            xAxis: [
                {
                    type: 'category',
                    boundaryGap: false,
                    axisLine: {
                        lineStyle: {
                            color: '#57617B',
                        },
                    },
                    data: ['OA', '邮箱', '云盘', '飞书', '论坛', '知识库'],
                }
            ],
            yAxis: [
                {
                    type: 'value',
                    name: '累计值',
                    min: 0,
                    max: 10000,
                    splitNumber: 5,
                    axisLine: {
                        lineStyle: {
                            color: '#57617B',
                        },
                    },
                    axisLabel: {
                        margin: 40,
                        textStyle: {
                            fontSize: 14,
                        },
                    },
                    splitLine: {
                        lineStyle: {
                            color: '#57617B',
                        },
                    },
                },
                {
                    type: 'value',
                    name: '净增值',
                    min: 0,
                    max: 1000,
                    splitNumber: 5,
                    axisLine: {
                        lineStyle: {
                            color: '#57617B',
                        },
                    },
                    axisLabel: {
                        margin: 40,
                        textStyle: {
                            fontSize: 14,
                        },
                    },
                    splitLine: {
                        lineStyle: {
                            color: '#57617B',
                        },
                    },
                },
            ],
            series: [
                {
                    name: '净增访问量 PV',
                    type: 'line',
                    smooth: true,
                    symbol: 'circle',
                    symbolSize: 5,
                    yAxisIndex: 1,
                    showSymbol: false,
                    lineStyle: {
                        normal: {
                            width: 1,
                        },
                    },
                    areaStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(
                                0,
                                0,
                                0,
                                1,
                                [
                                    {
                                        offset: 0,
                                        color: 'rgba(0, 204, 153, 0.3)',
                                    },
                                    {
                                        offset: 0.8,
                                        color: 'rgba(0, 204, 153,  0)',
                                    },
                                ],
                                false
                            ),
                            shadowColor: 'rgba(0, 204, 153, 0.1)',
                            shadowBlur: 10,
                        },
                    },
                    itemStyle: {
                        normal: {
                            color: 'rgb(0, 204, 153)',
                            borderColor: 'rgba(0, 204, 153,0.27)',
                            borderWidth: 12,
                        },
                    },
                    data: [800, 702, 691, 734, 850, 920],
                },
                {
                    name: '净增访问量 UV',
                    type: 'line',
                    smooth: true,
                    symbol: 'circle',
                    symbolSize: 5,
                    yAxisIndex: 1,
                    showSymbol: false,
                    lineStyle: {
                        normal: {
                            width: 1,
                        },
                    },
                    areaStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(
                                0,
                                0,
                                0,
                                1,
                                [
                                    {
                                        offset: 0,
                                        color: 'rgba(137, 189, 27, 0.3)',
                                    },
                                    {
                                        offset: 0.8,
                                        color: 'rgba(137, 189, 27, 0)',
                                    },
                                ],
                                false
                            ),
                            shadowColor: 'rgba(0, 0, 0, 0.1)',
                            shadowBlur: 10,
                        },
                    },
                    itemStyle: {
                        normal: {
                            color: 'rgb(137,189,27)',
                            borderColor: 'rgba(137,189,2,0.27)',
                            borderWidth: 12,
                        },
                    },
                    data: [520, 622, 511, 504, 690, 680],
                },
                {
                    name: '累计访问量 PV',
                    type: 'bar',
                    barWidth: 20,
                    itemStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [
                                {
                                    offset: 0,
                                    color: '#fccb05',
                                },
                                {
                                    offset: 1,
                                    color: '#f5804d',
                                },
                            ]),
                            barBorderRadius: 12,
                        },
                    },
                    data: [9000, 8400, 7300, 8700, 8200, 9400],
                },
                {
                    name: '累计访问量 UV',
                    type: 'bar',
                    barWidth: 20,
                    itemStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [
                                {
                                    offset: 0,
                                    color: '#8bd46e',
                                },
                                {
                                    offset: 1,
                                    color: '#09bcb7',
                                },
                            ]),
                            barBorderRadius: 11,
                        },
                    },
                    data: [6000, 5000, 6500, 7500, 6500, 7000],
                },
            ],
            options: [
                {
                    title: {
                        text: '1点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '2点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '3点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '4点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '5点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                },
                {
                    title: {
                        text: '6点流量统计'
                    },
                    series: [
                        {data: [1000, 3000, 2000, 4000, 4200, 3100]},
                        {data: [1000, 4000, 6000, 10000, 14200, 17300]},
                    ]
                }
            ]
        };
        theOption && echart.setOption(theOption);
    }

    render() {
        return (
            <div id='main' ref={element => {
                this.container = element
            }} style={{width: '630px', height: '428px',position:'absolute',top:'96px',left:'12px'}}/>
        );
    }
}

export default VisitorVolume;
