import React, {PureComponent} from 'react';
import {Button, Input, Space, Checkbox, Modal, PageHeader, Spin, Result, Tag} from "antd";
import {EyeInvisibleOutlined, EyeTwoTone, UserOutlined, LockOutlined, ScanOutlined} from '@ant-design/icons';
import loginStyle from './index.module.css';
import axios from 'axios';
import QRCode from 'qrcode.react';
import store from "../../redux/store";
import {saveToken} from "../../redux/actions/byToken";
import RandomCode from "../RandomCode";

class Login extends PureComponent {

    /**
     * 初始化state
     * {{loginLoading: 登录中, tip: 状态提示, codeLoading: 验证码获取状态, position: 二维码面板位置, codeState: 二维码获取状态}}
     */
    state = {
        loginLoading: false,
        codeLoading: false,
        tip: '获取验证码',
        position: {top: '500px', left: '400px'},
        codeState: 'waiting',
        tmpToken: '',
        isLogin: false
    };

    //定义全局定时器id变量
    timerID = 0;

    /**
     * 校验表单填写情况后向服务器发送登录请求
     */
    toLogin = () => {
        if (!this.uid.state.value || !this.pwd.state.value || this.auth.state.value!==store.getState().code) {
            console.log(this.auth);
            Modal.error({
                title: '错误！',
                okText: '确认',
                content: '请输入正确的账号、密码、验证码！'
            });
        } else {
            this.setState({loginLoading: true});
            const id = setTimeout(() => {
                Modal.error({
                    title: '错误！',
                    okText: '确认',
                    content: '网络连接超时！请检查您的网络配置，确保可以正常访问网络。',
                    afterClose: () => {
                        this.setState({loginLoading: false});
                    }
                });
            }, 15000);//15s后登录失败，弹框提示
            //发送网络请求
            axios({
                method: 'post',
                url: '/api/login',
                data: {userId: this.uid.state.value, pwd: this.pwd.state.value}
            }).then((resoponse) => {
                    clearTimeout(id);
                    //将token存档到redux，触发页面跳转
                    store.dispatch(saveToken(resoponse.data.token));
                }
            ).catch((error) => {
                console.log(error);
            });
        }
    }

    /**
     * 拉取验证码
     */

    getAuthCode = () => {
        this.setState({codeLoading: true});
        let timer = 60;
        this.setState({tip: `${timer}s后重新获取`});
        const id = setInterval(() => {
            timer = timer - 1;
            if (timer >= 0)
                this.setState({tip: `${timer}s后重新获取`});
            else {
                clearInterval(id);
                this.setState({tip: '获取验证码'});
                this.setState({codeLoading: false});
            }

        }, 1000);
    }

    requestQrCode=()=>{
        axios({
            method: 'post',
            url: '/api/qrcode',
        }).then((resoponse) => {
                this.setState({codeState: true, tmpToken: resoponse.data.token});
                clearTimeout(this.timerID);
                this.timerID = setInterval(() => {
                    axios({
                        method: 'post',
                        url: '/api/qrcode',
                        headers: {
                            Authorization: this.state.tmpToken
                        }
                    }).then((response) => {
                        if (response.data.code === 408) {
                            clearInterval(this.timerID);
                            alert(response.data.message);
                        } else if (response.data.code === 200 && response.data.result) {
                            clearInterval(this.timerID);
                            alert("登录成功！" + response.data.result);
                            //保存token到redux并执行跳转

                        } else if (response.data.code === 401) {
                            clearInterval(this.timerID);
                            alert("登录成功！" + response.data.message);
                        }
                    }).catch(err => {
                        clearInterval(this.timerID);
                        alert(err);
                    });
                }, 1000);
            }
        ).catch((error) => {
            Modal.error({
                title: '错误！',
                okText: '确认',
                content: error.message
            });
        });
    }

    /**
     * 拉取登录二维码，15s后超时
     */
    showCode = () => {
        this.setState({position: {top: '0px', left: '0px'}});
        this.timerID = setTimeout(() => {
            this.setState({codeState: false})
        }, 15000);
        //向服务器请求二维码，如果成功，清除定时器id，并生成二维码，每隔1S轮询一次二维码状态，将临时token加入到请求头中
        this.requestQrCode();
    }

    /**
     * 二维码获取失败后重新获取
     */
    againGetCode = () => {
        this.setState({codeState: 'waiting'});
        this.timerID = setTimeout(() => {
            this.setState({codeState: false})
        }, 15000);
        this.requestQrCode();
    }

    /**
     * 隐藏二维码面板，返回登录表单
     */
    hiddenCode = () => {
        this.setState({position: {top: '500px', left: '400px'}});
        //清除获取二维码时创建的定时器id；
        clearTimeout(this.timerID);
        this.setState({codeState: 'waiting'});
    }

    render() {
        return (
            <div className={loginStyle.login}>
                <div className={loginStyle.panel}>
                    <h3>用户登录</h3>
                    <Space size={32} direction='vertical'>
                        <Input type='text' ref={element => this.uid = element} size='large'
                               className={loginStyle.item} placeholder='请输入登录账号' prefix={<UserOutlined/>}/>
                        <Input.Password ref={element => this.pwd = element} className={loginStyle.item}
                                        size='large' placeholder='请输入登录密码' prefix={<LockOutlined/>}
                                        iconRender={visible => (visible ? <EyeTwoTone/> :
                                            <EyeInvisibleOutlined/>)}/>
                        <div>
                            <Space>
                                <Checkbox className={loginStyle.check}>记住登录状态</Checkbox>
                                <Checkbox className={loginStyle.check}>管理员登录</Checkbox>
                                
                            </Space>
                        </div>
                        <div>
                            <Input ref={element => this.auth = element} placeholder='输入验证码' size='large' style={{width:'200px',marginRight:'5px'}}/>
                            <RandomCode/>
                        </div>
                        <Button className={loginStyle.item} type='primary' size='large' onClick={this.toLogin}
                                loading={this.state.loginLoading}>登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录</Button>
                        <ScanOutlined className={loginStyle.scan} title='二维码登录' onClick={this.showCode}/>
                    </Space>
                </div>
                <div className={loginStyle.code} style={{
                    top: this.state.position.top,
                    left: this.state.position.left,
                }}>
                    <PageHeader title='返回' onBack={this.hiddenCode}/>
                    <br/>
                    {
                        //根据二维码获取的结果展示不同界面
                        this.state.codeState === 'waiting' ?
                            <div><Spin size='large'/><br/><br/><span>正在获取登录二维码，请稍候...</span></div> :
                            this.state.codeState ?
                                <div>
                                    <QRCode value={this.state.tmpToken} size={200} level='L'/>
                                    <br/>
                                    <br/>
                                    <p>请打开App并扫描二维码登录</p>
                                </div> :
                                <div>
                                    <Result status='error' title='错误' subTitle='获取登录二维码失败！请检查网络设置或尝试重新获取。'
                                            extra={[<Button type="primary" key='again'
                                                            onClick={this.againGetCode}>重试</Button>]}>
                                    </Result>
                                </div>
                    }
                </div>
            </div>
        );
    }

}

export default Login;
