import React from "react";
import axios from "axios";
import store from "../../redux/store";
import navMemberStyle from "./index.module.css";
import logo from "../../resource/userResource/logoIcon_tencent.png";
import {Avatar, Button, Space} from "antd";
import head from "../../resource/userResource/headIcon_183098.png";
import {saveUserInfo} from "../../redux/actions/byUserInfo";

class NavMember extends React.Component {

    componentDidMount() {
        console.log('成员导航栏挂载完成：', store.getState())
        axios({
            url: 'api/home',
            method: 'post',
            headers: {
                Authorization: store.getState().token
            }
        }).then(response => {
            store.dispatch(saveUserInfo(response.data));
            console.log('用户信息已返回:', store.getState());
        }).catch(err => {

        });
    }

    render() {
        return (
            <>
                <div className={navMemberStyle.header}>
                    <div className={navMemberStyle.left}>
                        <img src={logo}/>
                    </div>
                    <div className={navMemberStyle.right}>
                        <Space size={10}>
                            <img src={head}/>
                        </Space>
                    </div>
                </div>
            </>
        );
    }
}

export default NavMember;
