import React, {Component} from 'react';
import {Button, Form, Input, Modal, Space, Table, TreeSelect} from "antd";
import infoStyle from './index.module.css';
import {DeleteOutlined, EditOutlined, PlusOutlined} from "@ant-design/icons";

const {Column} = Table;


class GroupInfo extends Component {

    state = {width: '600px', visible: false, groupInfo: '', treeData: ''}

    componentDidMount() {
        this.setState({width: `${window.innerWidth - 600}px`});
        window.addEventListener('resize', () => {
            this.setState({width: `${window.innerWidth - 600}px`});
        });
    }

    editGroupInfo = (value) => {
        return () => {
            this.setState({visible: true, groupInfo: value}, () => {
                const data = this.props.list.concat([]);
                console.log(data);
                this.setState({treeData: this.transData(data)});
            });
        }
    }

    transData = (data) => {
        console.log('加工前的数据:', data);
        for (let i = 0; i < data.length; i++) {
            data[i].value = data[i].key;
            delete data[i].key;
            if (data[i].groupCode === this.state.groupInfo.key) {
                data.splice(i, 1);
                break;
            } else if (data[i].children) {
                this.transData(data[i].children);
            }
        }
    }

    render() {
        return (
            <div className={infoStyle.table} style={{width: this.state.width}}>
                <Table locale={{emptyText: '请选中组织后操作'}} dataSource={this.props.selected}
                       pagination={{hideOnSinglePage: true}}>
                    <Column title='组织名称' dataIndex='name' key='name' align='center' width='25%'/>
                    <Column title='上级组织' dataIndex='parent' key='parent' align='center' width='25%'/>
                    <Column title='成员数量' dataIndex='members' key='members' align='center' width='25%'/>
                    <Column title='操作' dataIndex='option' key='option' align='center' width='25%'
                            render={(_, record) => (
                                <Space size={15}>
                                    <Button icon={<EditOutlined/>} type='link' title='编辑'
                                            onClick={this.editGroupInfo(record)}/>
                                    <Button icon={<PlusOutlined/>} type='link' title='增加下级组织'/>
                                    <Button icon={<DeleteOutlined/>} type='link' title='删除'/>
                                </Space>
                            )}
                    />
                </Table>
                <Modal visible={this.state.visible} title='编辑组织' onCancel={() => {
                    this.setState({visible: false})
                }} maskClosable={false}>
                    <Form layout='horizontal'>
                        <Form.Item label='组织名称：' name='groupName' initialValue={this.state.groupInfo.name}>
                            <Input/>
                        </Form.Item>
                        <Form.Item label='上级组织' name='parent'>
                            <TreeSelect treeData={this.state.treeData}/>
                        </Form.Item>
                    </Form>
                </Modal>
            </div>
        );
    }
}

export default GroupInfo;
