import React, {Component} from 'react';
import {Button, Form, Input, Upload, Modal, Checkbox} from "antd";
import {EnvironmentOutlined, UploadOutlined} from "@ant-design/icons";
import axios from "axios";
import Map, {MapApiLoaderHOC} from 'react-bmapgl/Map';
import {NavigationControl, ZoomControl} from "react-bmapgl";

import {Status} from '../../resource/constant';
import NavCommon from "../../components/NavCommon";
import Footer from "../../components/Footer";
import Results from "./Results";
import registerStyle from './index.module.css';

const formItemLayout = {
    labelCol: {
        sm: {span: 5}
    },
    wrapperCol: {
        sm: {span: 17}
    }
}

const tailLayout = {
    wrapperCol: {offset: 5, span: 17}
}

class Sign extends Component {

    componentDidMount() {
        this.files = new FormData();
    }

    state = {
        codeState: {verify: '', message: ''},
        EmailState: {verify: '', message: ''},
        loading: false,
        percent: 0,
        result: '',
        map: false
    };

    verifyCode = () => ({
        validator(_, value) {
            return new Promise((resolve, reject) => {
                axios({
                    url: '/api/verifyOrgCode',
                    method: 'post',
                    data: {code: value}
                }).then(response => {
                    if (response.data.state === Status.success) {
                        reject('该企业已注册，不允许重复注册！');
                    } else {
                        resolve();
                    }
                }).catch(err => {
                        reject(err.message);
                    }
                );
            });
        }
    })

    verifyEmail = () => ({
        validator(_, value) {
            return new Promise((resolve, reject) => {
                axios({
                    url: '/api/verifyOrgEmail',
                    method: 'post',
                    data: {email: value}
                }).then(response => {
                    if (response.data.state === Status.success) {
                        reject('该邮箱已绑定企业，不允许重复绑定！');
                    } else {
                        resolve();
                    }
                }).catch(err => {
                        reject(err.message);
                    }
                );
            });
        }
    })

    verifyIdentity = () => ({
        validator(_, value) {
            return new Promise((resolve, reject) => {
                axios({
                    url: '/api/verifyIdentity',
                    method: 'post',
                    data: {identity: value}
                }).then(response => {
                    if (response.data.state === Status.failed) {
                        reject('该识别码已存在，请更换！');
                    } else {
                        resolve();
                    }
                }).catch(err => {
                        reject(err.message);
                    }
                );
            });
        }
    })

    readyLicense = file => {
        if (this.files.has('license')) {
            this.files.delete('license');
        }
        this.files.append('license', file,
            `${this.formRef.getFieldValue('orgCode')}_License${file.name.slice(file.name.lastIndexOf('.'))}`);
        return false;
    }

    readyAuthLeter = file => {
        if (!this.files.get('license') || this.files.has('authLeter')) {
            this.files.delete('authLeter');
        }
        this.files.append('authLeter', file,
            `${this.formRef.getFieldValue('orgCode')}_AuthLeter${file.name.slice(file.name.lastIndexOf('.'))}`);
        return false;
    }

    toRegister = (values) => {
        if (!this.clause.state.checked) {
            Modal.error({
                title: '错误！',
                okText: '确认！',
                content: '您必须同意服务条款后才能进行注册！'
            });
            return;
        }
        if (!this.files.get('authLeter') || !this.files.get('license')) {
            Modal.error({
                title: '错误！',
                okText: '确认！',
                content: '必须上传营业执照和法人授权函！'
            });
            return;
        }
        const {code, name, email, site, address, mobile, telephone, identity, pwd} = values;
        this.setState({loading: true});
        setTimeout(() => {
            this.setState({loading: false});
        }, 15000);
        axios({
            method: 'post',
            url: '/api/register',
            data: {code, name, email, site, address, mobile, telephone, identity, pwd}
        }).then(response => {
            if (response.data.state === Status.success) {
                axios({
                    url: '/api/upload',
                    method: 'post',
                    headers: {'Content-Type': 'multipart/form-data'},
                    data: this.files
                }).then(() => {
                    this.setState({result: Status.success})
                }).catch(err => {
                    alert(err.message);
                });
            }
        }).catch()
    }

    showMap = () => {
        this.setState({map: true}, () => {
            const cityControl = new window.BMapGL.CityListControl({
                anchor: window.BMapGL.BMAP_ANCHOR_TOP_RIGHT,
                offset: new window.BMapGL.Size(10, 5)
            });
            this.map.addControl(cityControl);
            const locationControl = new window.BMapGL.LocationControl({
                anchor: window.BMapGL.BMAP_ANCHOR_TOP_RIGHT,
            });
            this.map.addControl(locationControl);
            this.map.loadMapStyleFiles(()=>{
                new window.BMapGL.localCity({renderOptions:{}});
            });
        });
    }

    location=(value,_)=>{
        new  window.BMapGL.LocalSearch(this.map, {
            renderOptions:{map: this.map,panel:this.panel,pageCapacity:1}
        }).search(value);
    }

    getLocation=(e)=>{
        console.log(e);
    }

    render() {

        return (
            <>
                <NavCommon/>
                {
                    this.state.result === Status.success ? <Results statu={Status.success}/> :
                        <div className={registerStyle.frame}>
                            <img src={require("../../images/register_background.jpg").default}
                                 className={registerStyle.back}
                                 alt=''/>
                            <div className={registerStyle.content}>
                                <h2 className={registerStyle.title}>企业用户注册</h2>
                                <Form name='register' scrollToFirstError {...formItemLayout}
                                      size='large' ref={element => {
                                    this.formRef = element
                                }} validateTrigger={['onBlur']} onFinish={this.toRegister}>
                                    <Form.Item name='code' label='组织机构代码' hasFeedback validateFirst rules={
                                        [
                                            {
                                                required: true,
                                                message: '组织机构代码不合法！',
                                                type: 'string',
                                                pattern: /^[A-Z0-9]+$/,
                                                len: 18
                                            },
                                            this.verifyCode
                                        ]
                                    }>
                                        <Input allowClear onBlur={this.verifyOrgCode}/>
                                    </Form.Item>
                                    <Form.Item name='name' label='企业名称' hasFeedback
                                               rules={[{required: true, message: '企业名称不能为空！'}]}>
                                        <Input allowClear/>
                                    </Form.Item>
                                    <Form.Item name='email' label='管理邮箱' validateFirst
                                               rules={
                                                   [
                                                       {
                                                           required: true,
                                                           message: '邮箱地址不合法！',
                                                           type: 'email'
                                                       },
                                                       this.verifyEmail]
                                               }
                                               hasFeedback>
                                        <Input allowClear/>
                                    </Form.Item>
                                    <Form.Item name='site' label='企业官网'
                                               rules={[{required: true, message: '企业站点不合法！', type: 'url'}]}
                                               hasFeedback>
                                        <Input allowClear/>
                                    </Form.Item>
                                    <Form.Item name='address' label='企业地址'
                                               rules={[{required: true, message: '企业地址不能为空！'}]}
                                               hasFeedback>
                                        <Input allowClear style={{width: '92%'}}/><Button icon={<EnvironmentOutlined/>}
                                                                                          style={{
                                                                                              width: '8%',
                                                                                              float: 'right'
                                                                                          }} onClick={this.showMap}/>
                                    </Form.Item>
                                    <Form.Item name='orgLicense' getValueProps={() => {
                                        return 'fileList'
                                    }} label='营业执照' required>
                                        <Upload maxCount={1} accept='.png,.jpg,.pdf' className={registerStyle.upload}
                                                beforeUpload={this.readyLicense}>
                                            <Button icon={<UploadOutlined/>} block>上传文件</Button>
                                        </Upload>
                                    </Form.Item>
                                    <Form.Item name='orgAuthLeter' getValueProps={() => {
                                        return 'fileList'
                                    }} label='法人授权函' required>
                                        <Upload maxCount={1} accept='.png,.jpg,.pdf' className={registerStyle.upload}
                                                beforeUpload={this.readyAuthLeter}>
                                            <Button icon={<UploadOutlined/>} block>上传文件</Button>
                                        </Upload>
                                    </Form.Item>
                                    <Form.Item label='移动电话' name='mobile' hasFeedback rules={[{
                                        required: true,
                                        message: '移动电话号码为1开头的11位数字组合',
                                        type: 'string',
                                        pattern: /^1\d{10}$/
                                    }]}>
                                        <Input/>
                                    </Form.Item>
                                    <Form.Item label='固定电话' name='telephone' hasFeedback rules={[{
                                        required: true,
                                        message: '固定电话为“区号-座机号码”格式的11位或12为数字组合',
                                        type: "string",
                                        pattern: /\d{3}-\d{8}|\d{4}-\d{7}/
                                    }]}>
                                        <Input placeholder='格式为：区号-座机号码，例：010-12345678'/>
                                    </Form.Item>
                                    <Form.Item name='identity' label='识别码' hasFeedback validateFirst rules={
                                        [
                                            {
                                                required: true,
                                                message: '识别码包含大小写字母与数字的组合，长度为3-8位中间',
                                                type: 'string',
                                                pattern: /^[A-Za-z0-9]{3,8}$/
                                            },
                                            this.verifyIdentity
                                        ]
                                    }>
                                        <Input placeholder='识别码将作为区分企业的主要标识'/>
                                    </Form.Item>
                                    <Form.Item name='pwd' label='登录密码' rules={[{
                                        required: true,
                                        message: '密码必须包含大小写字母和数字的组合，长度在8-16位之间',
                                        type: 'string',
                                        pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,16}$/
                                    }]} hasFeedback>
                                        <Input.Password placeholder='请输入登录密码' allowClear autoComplete='off'/>
                                    </Form.Item>
                                    <Form.Item name='configPwd' label='确认密码' dependencies={['pwd']} hasFeedback
                                               rules={[
                                                   {
                                                       required: true,
                                                       message: '请再次输入密码并确保两次密码输入一致！',
                                                   },
                                                   ({getFieldValue}) => ({
                                                           validator(_, value) {
                                                               if (!value || getFieldValue('pwd') === value) {
                                                                   return Promise.resolve();
                                                               }
                                                               return Promise.reject('两次输入密码不一致！');
                                                           }
                                                       }
                                                   )
                                               ]}>
                                        <Input.Password placeholder='请再次输入登录密码' allowClear autoComplete='off'/>
                                    </Form.Item>
                                    <Form.Item wrapperCol={{offset: 5}} name='clause' getValueProps={() => {
                                        return 'checked';
                                    }}>
                                        <Checkbox ref={elment => {
                                            this.clause = elment
                                        }}>同意<a>飞云互联服务协议</a></Checkbox>
                                    </Form.Item>
                                    <Form.Item {...tailLayout}>
                                        <Button type='primary' htmlType='submit' style={{width: '100%'}}
                                                loading={this.state.loading}>提交</Button>
                                    </Form.Item>
                                </Form>
                            </div>
                        </div>
                }
                <Footer/>
                <Modal visible={this.state.map} width={800} bodyStyle={{height: '800px'}} title='在线地图'>
                    <div style={{width:'700px',margin:'10px auto'}}>
                        <Input.Search onSearch={this.location}/>
                    </div>
                    <Map ref={element => {
                        this.map = element.map
                    }} style={{width: '700px', height: '500px', margin: '10px auto'}}
                         enableScrollWheelZoom heading={0} tilt={40} zoom={12} maxZoom={20}
                         center={new window.BMapGL.Point(116.404449, 39.914889)}
                         onClick={this.getLocation}
                    >
                        <NavigationControl />
                        <ZoomControl />
                    </Map>
                    <div ref={element=>{this.panel=element}} style={{width:'700px',margin:'10px auto',height:'150px'}}/>
                </Modal>
            </>
        );
    }
}

