import React, {PureComponent} from 'react';
import {NavLink} from 'react-router-dom';
import headerStyle from './index.module.css';
import {Redirect, Route, Switch} from "react-router";
import Index from "../../pages/Index";
import {Avatar, Button} from "antd";
import {UserOutlined} from '@ant-design/icons';
import ProductionCenter from "../../pages/ProductCenter";
import Community from "../../pages/Community";
import Wiki from "../../pages/Wiki";
import store from "../../redux/store";
import logo from '../../resource/userResource/logoIcon_tencent.png';
import head from '../../resource/userResource/headIcon_183098.png';
import axios from "axios";
import {saveUserInfo} from '../../redux/actions/byUserInfo';
import {connect} from 'react-redux';


class HeaderUI extends PureComponent {
    render() {
        return (
            <>
                {
                    !store.getState().token ?
                        <NavCommon/> :
                        <NavMember/>
                }
            </>
        )
    }

}

class NavCommon extends React.Component {
    render() {
        return (
            <>
                <div className={headerStyle.header}>
                    <div className={headerStyle.left}>
                        <NavLink className={headerStyle.listItem} activeClassName={headerStyle.active}
                                 to='/index'>首页</NavLink>
                        <NavLink className={headerStyle.listItem} activeClassName={headerStyle.active}
                                 to='/product'>产品中心</NavLink>
                        <NavLink className={headerStyle.listItem} activeClassName={headerStyle.active}
                                 to='/community'>飞云社区</NavLink>
                        <NavLink className={headerStyle.listItem} activeClassName={headerStyle.active}
                                 to='/join'>加入飞云</NavLink>
                        <NavLink className={headerStyle.listItem} activeClassName={headerStyle.active}
                                 to='/wiki'>开发wiki</NavLink>
                    </div>
                    <div className={headerStyle.right}>
                        <Button type='link' size='large' icon={<UserOutlined/>}
                                className={headerStyle.sign}>企业注册</Button>
                        <label>token:{store.getState().token}</label>
                    </div>

                </div>
                <div>
                    <Switch>
                        <Route path='/index' component={Index}/>
                        <Route path='/product' component={ProductionCenter}/>
                        <Route path='/community' component={Community}/>
                        <Route path='/join' component={Index}/>
                        <Route path='/wiki' component={Wiki}/>
                        <Redirect to='/index'/>
                    </Switch>
                </div>
            </>
        );
    }
}

class NavMember extends React.Component {

    componentDidMount() {
        axios({
            url: 'api/home',
            method: 'post',
            headers: {
                Authorization: store.getState().token
            }
        }).then(response => {
            store.dispatch(saveUserInfo(response.data));
            console.log(store.getState().userInfo);
        }).catch(err => {

        });
    }

    render() {
        return (
            <>
                <div className={headerStyle.header}>
                    <div className={headerStyle.left}>
                        <img src={logo}/>
                    </div>
                    <div className={headerStyle.right}>
                        <Avatar src={head}/>
                        <label>&nbsp;骆志军</label>
                    </div>
                </div>
            </>
        );
    }
}

export default Header;
