import React, {PureComponent} from 'react';
import {Button, Input, Space, Checkbox, Modal,PageHeader} from "antd";
import {EyeInvisibleOutlined, EyeTwoTone, UserOutlined, LockOutlined,ScanOutlined} from '@ant-design/icons';
import loginStyle from './index.module.css';

class LoginPanel extends PureComponent {

    state = {loginLoading: false, codeLoading: false, tip: '获取验证码',position:{top:'500px',left:'400px'}};
    toLogin = () => {
        if (!this.uid.value || !this.pwd.value || !this.auth.value) {
            Modal.error({
                title: '错误！',
                okText: '确认',
                content: '登录账号及密码均不能为空，并请输入验证码！',
                afterClose: () => {

                }
            });
        } else {
            this.setState({loginLoading: true});
            const id = setTimeout(() => {
                Modal.error({
                    title: '错误！',
                    okText: '确认',
                    content: '网络连接超时！请检查您的网络配置，确保可以正常访问网络。',
                    afterClose: () => {
                        this.setState({loginLoading: false});
                    }
                });
            }, 15000);//15s后登录失败，弹框提示

            //发送网络请求
        }
    }

    getAuthCode = () => {
        this.setState({codeLoading: true});
        let timer = 60;
        this.setState({tip: `${timer}s后重新获取`});
        const id = setInterval(() => {
            timer = timer - 1;
            if (timer >= 0)
                this.setState({tip: `${timer}s后重新获取`});
            else {
                clearInterval(id);
                this.setState({tip: '获取验证码'});
                this.setState({codeLoading: false});
            }

        }, 1000);
    }

    render() {
        return (
            <div className={loginStyle.login}>
                <form className={loginStyle.form}>
                    <h3>用户登录</h3>
                    <Space size={32} direction='vertical'>
                        <Input ref={element => this.uid = element} size='large' name='userId' className={loginStyle.item}
                               placeholder='请输入登录账号' prefix={<UserOutlined/>}/>
                        <Input.Password ref={element => this.pwd = element} name='userPwd' className={loginStyle.item}
                                        size='large' placeholder='请输入登录密码'
                                        prefix={<LockOutlined/>}
                                        iconRender={visible => (visible ? <EyeTwoTone/> : <EyeInvisibleOutlined/>)}/>
                        <div>
                            <Checkbox className={loginStyle.check}>记住登录状态</Checkbox>
                            <Button type='link' className={loginStyle.link}>忘记密码</Button>
                        </div>
                        <Input.Search ref={element => this.auth = element} placeholder='输入验证码' enterButton={this.state.tip}
                                      size='large'
                                      loading={this.state.codeLoading}
                                      onSearch={this.getAuthCode}/>
                        <Button className={loginStyle.item} type='primary'
                                size='large' onClick={this.toLogin}
                                loading={this.state.loginLoading}>登&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;录</Button>
                        <ScanOutlined className={loginStyle.scan} title='二维码登录'/>
                    </Space>
                </form>
                <div className={loginStyle.code} style={"top:this.state.position.top"}>
                    <PageHeader title='返回'/>
                </div>
            </div>
        );
    }
}

export default LoginPanel;
