import {OPTIONS} from '../../resource/constant';

/**
 * 初始化token值
 * @type {string}
 */
const initToken='';

export default function token(preState=initToken,action){
    const {type,data}=action;
    switch (type){
        case OPTIONS.saveToken:
            return data;
        default:
            return preState;
    }
}
