import React, {Component} from 'react';
import {Button, Space, Spin, Table, Tree, Input, Modal, Form} from "antd";
import axios from "axios";
import {ApartmentOutlined, DeleteOutlined, EditOutlined, PlusOutlined} from "@ant-design/icons";
import Store from "../../../../redux/store";
import addGroupStyle from './index.module.css';

const {store} = Store;
const {Column} = Table;
const {Search} = Input;

class AddGroups extends Component {

    /**
     * 构造器，向服务器请求组织信息
     * @param props
     */
    constructor(props) {
        super(props);
        this.state = {loading: true, option: '', enableGroup: []};
        axios({
            url: 'http://localhost:3000/api/orgGroup',
            method: 'post',
            headers: {authorization: store.getState().token}
        }).then(response => {
            const {groups} = response.data;
            const newGroup = groups.concat();
            let treeData = '';

            //加工组织数据
            for (let i = 0; i < newGroup.length; i++) {
                if (newGroup[i].parent) {
                    for (let j = i + 1; j < newGroup.length; j++) {
                        if (newGroup[j].groupCode === newGroup[i].parent) {
                            if (!newGroup[j].children) {
                                newGroup[j].children = [];
                            }
                            let tmp = '';
                            if (newGroup[i].children) {
                                tmp = {
                                    title: newGroup[i].groupName,
                                    key: newGroup[i].groupCode,
                                    children: newGroup[i].children
                                }
                            } else {
                                tmp = {
                                    title: newGroup[i].groupName,
                                    key: newGroup[i].groupCode
                                }
                            }
                            newGroup[j].children.push(tmp);
                        }
                    }
                } else {
                    treeData = [{
                        title: newGroup[i].groupName,
                        key: newGroup[i].groupCode,
                        children: newGroup[i].children ? newGroup[i].children : []
                    }];
                }
            }
            this.setState({loading: false, groups, treeData});
        }).catch();
    }

    /**
     * 展示被选中的组织信息
     * @param selectedKeys 被选中的key数组
     */
    showGroupInfo = (selectedKeys) => {
        if (selectedKeys.length === 0)
            return;
        const {groups} = this.state;
        let selected = {};
        for (let i = 0; i < groups.length; i++) {
            if (groups[i].groupCode === selectedKeys[0]) {
                selected.groupName = groups[i].groupName;
                selected.members = groups[i].groupMembers;
                selected.key = groups[i].groupCode;
                selected.parent = '--';
                const parent = groups[i].parent;
                for (let j = 0; j < groups.length; j++) {
                    if (groups[j].groupCode === parent) {
                        selected.parent = groups[j].groupName;
                        break;
                    }
                }
                break;
            }

        }
        this.setState({selected});
    }

    showEditGroup = () => {
        const {selected, groups} = this.state;
        console.log('selected', selected)
        console.log('groups', groups)
        if (selected.key === groups[0].groupCode) {
            const group = {title: groups[0].groupName, value: groups[0].groupCode};
            this.setState({option: 'edit', enableGroup: [group]});
            return;
        }
        const tmp = groups.filter(item => {
            return !item.groupCode.includes(selected.key);
        });
        console.log('tmp',tmp);
        for (let i = 0; i < tmp.length; i++) {
            console.log(tmp[i].parent);
            if (tmp[i].parent) {
                for (let j = i + 1; j < tmp.length; i++) {
                    if (tmp[j].groupCode === tmp.parent) {
                        if (!tmp[j].children)
                            tmp[j].children = [];
                        if (tmp[i].children) {
                            tmp[j].children.push({
                                title: tmp[i].groupName,
                                value: tmp[i].groupCode,
                                children: tmp[i].children
                            });
                        } else {
                            tmp[j].children.push({
                                title: tmp[i].groupName,
                                value: tmp[i].groupCode
                            });
                        }
                    }
                }
            } else {
                this.setState({
                    option: 'edit', enableGroup: [{
                        title: tmp[i].groupName,
                        value: tmp[i].groupCode,
                        children: tmp[i].children?tmp[i].children:[]
                    }]
                },()=>{
                    console.log('enableGroup',this.state.enableGroup);
                });
                break;
            }
        }

    }


    render() {
        return (
            <div className={addGroupStyle.container}>
                <div className={addGroupStyle.left}>
                    <Search placeholder='搜索组织'/>
                    <div style={{marginTop: '10px'}}>
                        {
                            this.state.loading ?
                                <Spin tip='正在获取组织信息，请稍候......'/> :
                                <Tree treeData={this.state.treeData} icon={<ApartmentOutlined/>} showIcon={true}
                                      showLine={{showLeafIcon: false}} onSelect={this.showGroupInfo}/>
                        }
                    </div>
                </div>
                <div className={addGroupStyle.info}>
                    <Table locale={{emptyText: '请选中具体组织后进行操作！'}} pagination={{hideOnSinglePage: true}}
                           dataSource={this.state.selected ? [this.state.selected] : []}>
                        <Column title='组织名称' dataIndex='groupName' key='groupName' align='center' width='25%'/>
                        <Column title='上级组织' dataIndex='parent' key='parent' align='center' width='25%'/>
                        <Column title='成员数量' dataIndex='members' key='members' align='center' width='25%'/>
                        <Column title='操作' dataIndex='option' align='center' width='25%'
                                render={() => (
                                    <Space size={15}>
                                        <Button type='link' icon={<EditOutlined/>} title='编辑组织'
                                                onClick={this.showEditGroup}/>
                                        <Button type='link' icon={<PlusOutlined/>} title='增加下级组织'/>
                                        <Button type='link' icon={<DeleteOutlined/>} title='删除组织'/>
                                    </Space>
                                )}/>
                    </Table>
                </div>
                <Modal title='编辑组织' maskClosable={false} visible={this.state.option === 'edit'} okText='确认'
                       cancelText='取消' onCancel={() => {
                    this.setState({option: ''})
                }}>
                    <Form>
                        <Form.Item label='组织名称'>
                            <Input value={this.state.selected ? this.state.selected.groupName : ''}/>
                        </Form.Item>
                        <Form.Item label='上级组织'>

                        </Form.Item>
                    </Form>
                </Modal>
            </div>
        );
    }
}

export default AddGroups;
